import sgMail from '@sendgrid/mail';

// Configuration SendGrid - Lazy initialization
let isConfigured = false;

function configureSendGrid() {
  if (!isConfigured && process.env.SENDGRID_API_KEY) {
    sgMail.setApiKey(process.env.SENDGRID_API_KEY);
    isConfigured = true;
  } else if (!process.env.SENDGRID_API_KEY) {
    console.warn('⚠️  SENDGRID_API_KEY is not configured - email features will be disabled');
  }
}

export interface EmailTemplate {
  subject: string;
  html: string;
  text: string;
}

export interface EmailData {
  to: string;
  toName?: string;
  subject: string;
  html: string;
  text: string;
  fromEmail?: string;
  fromName?: string;
}

class EmailService {
  private fromEmail: string;
  private fromName: string;
  private appUrl: string;

  constructor() {
    this.fromEmail = process.env.SENDGRID_FROM_EMAIL || 'noreply@TeamMoves.com';
    this.fromName = process.env.SENDGRID_FROM_NAME || 'TeamMoves';
    this.appUrl = process.env.APP_URL || 'https://teammove.fr';
  }

  async sendEmail(data: EmailData): Promise<boolean> {
    try {
      configureSendGrid();

      if (!process.env.SENDGRID_API_KEY) {
        console.warn('Email not sent - SENDGRID_API_KEY not configured');
        return false;
      }

      const msg = {
        to: {
          email: data.to,
          name: data.toName || data.to
        },
        from: {
          email: data.fromEmail || this.fromEmail,
          name: data.fromName || this.fromName
        },
        subject: data.subject,
        text: data.text,
        html: data.html,
      };

      await sgMail.send(msg);
      console.log('Email sent successfully to:', data.to);
      return true;
    } catch (error) {
      console.error('Failed to send email:', error);
      return false;
    }
  }

  // Template pour reset de mot de passe
  async sendPasswordResetEmail(email: string, name: string, resetToken: string): Promise<boolean> {
    const resetUrl = `${this.appUrl}/reset-password?token=${resetToken}`;

    const html = `
      <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto; padding: 20px;">
        <div style="background-color: #f8f9fa; padding: 30px; border-radius: 8px; text-align: center;">
          <h1 style="color: #333; margin-bottom: 30px;">Réinitialisation de votre mot de passe</h1>
          <p style="color: #666; font-size: 16px; margin-bottom: 30px;">
            Bonjour ${name},<br><br>
            Vous avez demandé la réinitialisation de votre mot de passe. 
            Cliquez sur le bouton ci-dessous pour créer un nouveau mot de passe.
          </p>
          <a href="${resetUrl}" style="display: inline-block; background-color: #007bff; color: white; padding: 12px 30px; text-decoration: none; border-radius: 5px; font-weight: bold; margin-bottom: 20px;">
            Réinitialiser le mot de passe
          </a>
          <p style="color: #666; font-size: 14px; margin-top: 30px;">
            Ce lien expirera dans 1 heure. Si vous n'avez pas demandé cette réinitialisation, ignorez cet email.
          </p>
          <p style="color: #666; font-size: 12px; margin-top: 20px;">
            Si le bouton ne fonctionne pas, copiez ce lien dans votre navigateur :<br>
            ${resetUrl}
          </p>
        </div>
      </div>
    `;

    const text = `
      Réinitialisation de votre mot de passe

      Bonjour ${name},

      Vous avez demandé la réinitialisation de votre mot de passe.
      Cliquez sur ce lien pour créer un nouveau mot de passe :

      ${resetUrl}

      Ce lien expirera dans 1 heure. Si vous n'avez pas demandé cette réinitialisation, ignorez cet email.
    `;

    return await this.sendEmail({
      to: email,
      toName: name,
      subject: 'Réinitialisation de votre mot de passe',
      html,
      text
    });
  }

  // Template pour invitation à un événement
  async sendEventInvitationEmail(
    email: string, 
    eventName: string, 
    organizationName: string, 
    eventDate: Date, 
    invitationToken: string,
    meetingPoint?: string,
    destination?: string,
