    title: 'Événement créé',
    message: `L'événement "${eventName}" a été créé avec succès. Les participants peuvent maintenant s'inscrire.`,
  }),

  WELCOME: (organizationName: string) => ({
    type: 'success' as const,
    title: 'Bienvenue sur TeamMove !',
