                eventParticipants.forEach(p => {
                  if (p.email) allParticipantEmails.add(p.email);
                });
              } catch (err) {
                console.error(`Erreur récupération participants événement ${orgEvent.id}:`, err);
              }
            }
          }

          if (allParticipantEmails.size > 0 && emailConfig.canSendEmails) {
