import cron from 'node-cron';
import { SubscriptionService } from './subscription-service';
import { EmailService } from './email-service';
import { getDaysUntilExpiry, validateSubscription } from './subscription-config';

// Import du storage - sera résolu à l'exécution
let storage: any;

export interface ReminderConfig {
  enabled: boolean;
  daysBeforeExpiry: number[];
  emailTemplate?: string;
  notificationTypes: ('email' | 'dashboard')[];
}

// Configuration par défaut des rappels
export const DEFAULT_REMINDER_CONFIG: ReminderConfig = {
  enabled: true,
  daysBeforeExpiry: [7, 3, 1], // Rappels à 7, 3 et 1 jour(s)
  notificationTypes: ['email', 'dashboard']
};

export class SchedulerService {
  private static initialized = false;
  private static reminderConfig: ReminderConfig = DEFAULT_REMINDER_CONFIG;

  // Initialiser le service de planification
  static async initialize(): Promise<void> {
    if (this.initialized) return;

    try {
      const { storage: storageInstance } = await import('./storage');
      storage = storageInstance;

      // Initialiser les services dépendants
      await SubscriptionService.initialize();

      // Démarrer les tâches programmées
      this.startScheduledTasks();

      this.initialized = true;
      console.log('✅ Scheduler service initialized successfully');
    } catch (error) {
      console.error('❌ Failed to initialize scheduler service:', error);
    }
  }

  // Démarrer toutes les tâches programmées
  private static startScheduledTasks(): void {
    // Vérifier les abonnements expirés tous les jours à 9h00
    cron.schedule('0 9 * * *', async () => {
      console.log('🔄 Running daily subscription check...');
      await this.checkExpiredSubscriptions();
    });

    // Envoyer les rappels de renouvellement tous les jours à 10h00
    cron.schedule('0 10 * * *', async () => {
      console.log('🔔 Running daily renewal reminders...');
      await this.sendRenewalReminders();
    });

    // Nettoyer les données anciennes toutes les semaines (dimanche à 2h00)
    cron.schedule('0 2 * * 0', async () => {
      console.log('🧹 Running weekly cleanup...');
      await this.cleanupOldData();
    });

    // Générer les statistiques mensuelles (1er du mois à 8h00)
    cron.schedule('0 8 1 * *', async () => {
      console.log('📊 Generating monthly statistics...');
      await this.generateMonthlyStats();
    });

    console.log('⏰ Scheduled tasks started');
  }

  // Vérifier les abonnements expirés et les désactiver
  static async checkExpiredSubscriptions(): Promise<void> {
    try {
      console.log('🔍 Checking for expired subscriptions...');

      const organizations = await storage.getAllOrganizations();
      let expiredCount = 0;

      for (const org of organizations) {
        // Ignorer les comptes admin et découverte
        if (org.role === 'admin' || org.subscriptionType === 'decouverte') {
          continue;
        }

        const validation = validateSubscription(org);

        if (!validation.isValid && validation.needsRenewal) {
          console.log(`⚠️ Subscription expired for organization ${org.id} (${org.name})`);

          // Rétrograder vers découverte
          await SubscriptionService.cancelSubscription(org.id);

          // Créer une notification dashboard
          await this.createExpiryNotification(org);

          expiredCount++;
        }
      }

      console.log(`✅ Subscription check completed. ${expiredCount} subscriptions expired.`);
    } catch (error) {
      console.error('❌ Error checking expired subscriptions:', error);
    }
  }

  // Envoyer les rappels de renouvellement
  static async sendRenewalReminders(): Promise<void> {
    if (!this.reminderConfig.enabled) {
      console.log('ℹ️ Renewal reminders disabled');
      return;
    }

    try {
      console.log('🔔 Sending renewal reminders...');

      const organizations = await storage.getAllOrganizations();
      let remindersSent = 0;

      for (const org of organizations) {
        // Ignorer les comptes admin et découverte
        if (org.role === 'admin' || org.subscriptionType === 'decouverte') {
          continue;
        }

        const daysUntilExpiry = getDaysUntilExpiry(org);

        if (daysUntilExpiry !== null && daysUntilExpiry > 0) {
          // Vérifier si on doit envoyer un rappel
          const shouldSendReminder = this.reminderConfig.daysBeforeExpiry.includes(daysUntilExpiry);

          if (shouldSendReminder) {
            // Vérifier si on n'a pas déjà envoyé ce rappel aujourd'hui
            const lastReminder = await this.getLastReminderSent(org.id, daysUntilExpiry);

            if (!lastReminder || this.isNewDay(lastReminder)) {
              await this.sendReminderToOrganization(org, daysUntilExpiry);
              await this.recordReminderSent(org.id, daysUntilExpiry);
              remindersSent++;
            }
          }
        }
      }

      console.log(`✅ Renewal reminders completed. ${remindersSent} reminders sent.`);
    } catch (error) {
      console.error('❌ Error sending renewal reminders:', error);
    }
  }

  // Envoyer un rappel à une organisation
  private static async sendReminderToOrganization(org: any, daysLeft: number): Promise<void> {
    try {
      const reminderTypes = this.reminderConfig.notificationTypes;

      // Email reminder
      if (reminderTypes.includes('email')) {
        await EmailService.sendRenewalReminder(org);
      }

      // Dashboard notification
      if (reminderTypes.includes('dashboard')) {
        await this.createReminderNotification(org, daysLeft);
      }

      console.log(`📧 Reminder sent to ${org.name} (${daysLeft} days left)`);
    } catch (error) {
      console.error(`❌ Error sending reminder to ${org.id}:`, error);
    }
  }

  // Créer une notification de rappel dans le dashboard
  private static async createReminderNotification(org: any, daysLeft: number): Promise<void> {
    try {
      const message = daysLeft === 1 
        ? 'Votre abonnement expire demain !'
        : `Votre abonnement expire dans ${daysLeft} jours.`;

      await storage.createNotification({
        organizationId: org.id,
        type: 'warning',
        title: '🔔 Rappel de renouvellement',
        message: message + ' Renouvelez dès maintenant pour éviter toute interruption.',
        actionUrl: '/dashboard/billing',
      });
    } catch (error) {
      console.error('Error creating reminder notification:', error);
    }
  }

  // Créer une notification d'expiration
  private static async createExpiryNotification(org: any): Promise<void> {
    try {
      await storage.createNotification({
        organizationId: org.id,
        type: 'error',
        title: '⚠️ Abonnement expiré',
        message: 'Votre abonnement a expiré et vous avez été basculé vers l\'offre Découverte. Renouvelez pour retrouver toutes vos fonctionnalités.',
        actionUrl: '/pricing',
      });
    } catch (error) {
      console.error('Error creating expiry notification:', error);
    }
  }

  // Nettoyer les anciennes données
  static async cleanupOldData(): Promise<void> {
    try {
      console.log('🧹 Starting data cleanup...');

      // Supprimer les anciens tokens expirés (plus de 30 jours)
      const thirtyDaysAgo = new Date();
      thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

      await storage.cleanupExpiredTokens(thirtyDaysAgo);

      // Supprimer les anciennes notifications lues (plus de 90 jours)
      const ninetyDaysAgo = new Date();
      ninetyDaysAgo.setDate(ninetyDaysAgo.getDate() - 90);

      await storage.cleanupOldNotifications(ninetyDaysAgo);

      // Supprimer les anciens logs de rappels (plus de 180 jours)
      const oneEightyDaysAgo = new Date();
      oneEightyDaysAgo.setDate(oneEightyDaysAgo.getDate() - 180);

      await this.cleanupOldReminders(oneEightyDaysAgo);

      console.log('✅ Data cleanup completed');
    } catch (error) {
      console.error('❌ Error during cleanup:', error);
    }
  }

  // Générer les statistiques mensuelles
  static async generateMonthlyStats(): Promise<void> {
    try {
      console.log('📊 Generating monthly statistics...');

      const stats = await this.calculateMonthlyMetrics();

      // Sauvegarder les statistiques
      await storage.saveMonthlyStats(stats);

      // Envoyer rapport aux admins si configuré
      if (process.env.ADMIN_EMAIL) {
        await this.sendMonthlyReportToAdmins(stats);
      }

      console.log('✅ Monthly statistics generated');
    } catch (error) {
      console.error('❌ Error generating statistics:', error);
    }
  }

  // Calculer les métriques mensuelles
  private static async calculateMonthlyMetrics(): Promise<any> {
    const now = new Date();
    const lastMonth = new Date(now.getFullYear(), now.getMonth() - 1, 1);
    const thisMonth = new Date(now.getFullYear(), now.getMonth(), 1);

    const organizations = await storage.getAllOrganizations();

    const stats = {
      period: `${lastMonth.getFullYear()}-${String(lastMonth.getMonth() + 1).padStart(2, '0')}`,
      totalOrganizations: organizations.length,
      activeSubscriptions: organizations.filter(org => org.subscriptionType !== 'decouverte' && org.subscriptionStatus === 'active').length,
      newSubscriptions: 0, // À calculer avec les dates de création
      subscriptionsByType: {
        decouverte: organizations.filter(org => org.subscriptionType === 'decouverte').length,
        evenementielle: organizations.filter(org => org.subscriptionType === 'evenementielle').length,
        pro_club: organizations.filter(org => org.subscriptionType === 'pro_club').length,
        pro_pme: organizations.filter(org => org.subscriptionType === 'pro_pme').length,
        pro_entreprise: organizations.filter(org => org.subscriptionType === 'pro_entreprise').length,
      },
      totalRevenue: 0, // À calculer
      eventsCreated: 0, // À récupérer depuis la base
      invitationsSent: 0, // À récupérer depuis la base
      generatedAt: new Date(),
    };

    return stats;
  }

  // Envoyer le rapport mensuel aux admins
  private static async sendMonthlyReportToAdmins(stats: any): Promise<void> {
    try {
      const subject = `📊 Rapport mensuel TeamMove - ${stats.period}`;
      const message = `
        Voici le rapport mensuel des activités TeamMove :

        📈 Organisations totales : ${stats.totalOrganizations}
        💼 Abonnements actifs : ${stats.activeSubscriptions}
        🆕 Nouveaux abonnements : ${stats.newSubscriptions}

        Répartition par offre :
        - Découverte : ${stats.subscriptionsByType.decouverte}
        - Événementielle : ${stats.subscriptionsByType.evenementielle}
        - Pro Club : ${stats.subscriptionsByType.pro_club}
        - Pro PME : ${stats.subscriptionsByType.pro_pme}
        - Pro Entreprise : ${stats.subscriptionsByType.pro_entreprise}

        📅 Événements créés : ${stats.eventsCreated}
        📧 Invitations envoyées : ${stats.invitationsSent}
      `;

      await EmailService.sendNotificationEmail(
        process.env.ADMIN_EMAIL!,
        subject,
        message,
        `${process.env.APP_URL}/admin/stats`,
        'Voir les détails'
      );
    } catch (error) {
      console.error('❌ Error sending monthly report:', error);
    }
  }

  // Méthodes utilitaires pour le tracking des rappels
  private static async getLastReminderSent(orgId: string, daysLeft: number): Promise<Date | null> {
    // Implémentation simplifiée - en pratique, cela irait chercher dans une table de logs
    return null;
  }

  private static async recordReminderSent(orgId: string, daysLeft: number): Promise<void> {
    // Implémentation simplifiée - en pratique, cela sauvegarderait dans une table de logs
    console.log(`📝 Recording reminder sent: org=${orgId}, days=${daysLeft}`);
  }

  private static isNewDay(lastDate: Date): boolean {
    const now = new Date();
    return now.getDate() !== lastDate.getDate() || 
           now.getMonth() !== lastDate.getMonth() || 
           now.getFullYear() !== lastDate.getFullYear();
  }

  private static async cleanupOldReminders(cutoffDate: Date): Promise<void> {
    // Implémentation pour nettoyer les anciens logs de rappels
    console.log(`🗑️ Cleaning up reminders older than ${cutoffDate}`);
  }

  // Configuration dynamique des rappels
  static updateReminderConfig(config: Partial<ReminderConfig>): void {
    this.reminderConfig = { ...this.reminderConfig, ...config };
    console.log('⚙️ Reminder configuration updated:', this.reminderConfig);
  }

  static getReminderConfig(): ReminderConfig {
    return { ...this.reminderConfig };
  }

  // Forcer l'exécution des tâches (pour les tests ou l'admin)
  static async runTaskNow(taskName: string): Promise<void> {
    switch (taskName) {
      case 'checkExpired':
        await this.checkExpiredSubscriptions();
        break;
      case 'sendReminders':
        await this.sendRenewalReminders();
        break;
      case 'cleanup':
        await this.cleanupOldData();
        break;
      case 'generateStats':
        await this.generateMonthlyStats();
        break;
      default:
        throw new Error(`Unknown task: ${taskName}`);
    }
  }

  // Obtenir le statut du scheduler
  static getStatus(): { initialized: boolean; config: ReminderConfig; nextRuns: any[] } {
    return {
      initialized: this.initialized,
      config: this.reminderConfig,
      nextRuns: [] // En pratique, on retournerait les prochaines exécutions des tâches cron
    };
  }
}

server/storage.ts.content:
import {
  organizations,
  events,
  eventParticipants,
  eventInvitations,
  messages,
  notifications,
  passwordResetTokens,
  participantChangeRequests,
  emailReplyTokens,
  type Organization,
  type InsertOrganization,
  type Event,
  type InsertEvent,
  type EventParticipant,
  type InsertEventParticipant,
  type EventInvitation,
  type InsertEventInvitation,
  type Message,
  type InsertMessage,
  type Notification,
  type InsertNotification,
  type PasswordResetToken,
  type InsertPasswordResetToken,
  type ParticipantChangeRequest,
  type InsertParticipantChangeRequest,
  type EmailReplyToken,
  type InsertEmailReplyToken,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, or, sql, isNull } from "drizzle-orm";
import { randomUUID } from "crypto";

export interface IStorage {
  // Organizations
  getOrganization(id: string): Promise<Organization | undefined>;
  getOrganizationByEmail(email: string): Promise<Organization | undefined>;
  getOrganizations(): Promise<Organization[]>;
  createOrganization(org: InsertOrganization): Promise<Organization>;
  updateOrganization(id: string, data: Partial<InsertOrganization>): Promise<Organization>;
  updateOrganizationPassword(email: string, hashedPassword: string): Promise<void>;

  // Admin methods
  getAllOrganizationsForAdmin(): Promise<Organization[]>;
  updateOrganizationStatus(id: string, isActive: boolean): Promise<Organization>;
  updateOrganizationFeatures(id: string, features: string[]): Promise<Organization>;
  deleteOrganization(id: string): Promise<void>;

  // Events
  getEvent(id: string): Promise<Event | undefined>;
  getEvents(): Promise<Event[]>;
  getEventsByOrganization(organizationId: string): Promise<Event[]>;
  createEvent(event: InsertEvent): Promise<Event>;
  updateEvent(id: string, data: Partial<InsertEvent>): Promise<Event>;
  deleteEvent(id: string): Promise<void>;

  // Event Participants
  getEventParticipant(id: string): Promise<EventParticipant | undefined>;
  getEventParticipants(eventId: string): Promise<EventParticipant[]>;
  addEventParticipant(participant: InsertEventParticipant): Promise<EventParticipant>;
  createEventParticipant(participant: InsertEventParticipant): Promise<EventParticipant>;
  removeEventParticipant(id: string): Promise<void>;
  updateEventParticipant(id: string, data: Partial<InsertEventParticipant>): Promise<EventParticipant>;

  // Event Invitations
  getEventInvitation(token: string): Promise<EventInvitation | undefined>;
  getEventInvitations(eventId: string): Promise<EventInvitation[]>;
  createEventInvitation(invitation: InsertEventInvitation): Promise<EventInvitation>;
  updateEventInvitation(id: string, data: Partial<InsertEventInvitation>): Promise<EventInvitation>;

  // Messages
  getEventMessages(eventId: string): Promise<Message[]>;
  getMessage(id: string): Promise<Message | undefined>;
  createMessage(message: InsertMessage): Promise<Message>;
  deleteMessage(id: string): Promise<void>;

  // Password Reset Tokens
  createPasswordResetToken(token: InsertPasswordResetToken): Promise<PasswordResetToken>;
  getPasswordResetToken(token: string): Promise<PasswordResetToken | undefined>;
  markPasswordResetTokenAsUsed(id: string): Promise<void>;
  cleanupExpiredTokens(): Promise<void>;

  // Notifications
  getNotificationsByOrganization(organizationId: string): Promise<Notification[]>;
  createNotification(notification: InsertNotification): Promise<Notification>;
  markNotificationAsRead(id: string, organizationId: string): Promise<void>;
  markAllNotificationsAsRead(organizationId: string): Promise<void>;
  deleteNotification(id: string, organizationId: string): Promise<void>;

  // Participant Change Requests
  createParticipantChangeRequest(request: InsertParticipantChangeRequest): Promise<ParticipantChangeRequest>;
  getParticipantChangeRequestsByEvent(eventId: string): Promise<ParticipantChangeRequest[]>;
  getParticipantChangeRequest(id: string): Promise<ParticipantChangeRequest | undefined>;
  updateParticipantChangeRequest(id: string, data: Partial<InsertParticipantChangeRequest>): Promise<ParticipantChangeRequest>;

  // Email Reply Tokens
  createEmailReplyToken(token: InsertEmailReplyToken): Promise<EmailReplyToken>;
  getEmailReplyToken(token: string): Promise<EmailReplyToken | undefined>;
  deactivateEmailReplyToken(token: string): Promise<void>;
  getReplyTokenData(token: string): Promise<any>;
  markReplyTokenAsUsed(token: string): Promise<void>;

  // Subscription limits
  checkEventLimit(organizationId: string): Promise<{ canCreate: boolean; currentCount: number; limit?: number }>;
  checkInvitationLimit(eventId: string): Promise<{ canCreate: boolean; currentCount: number; limit?: number }>;
  getSubscriptionLimits(organizationId: string): Promise<{ events: { current: number; limit?: number }; invitations: { current: number; limit?: number } }>;
}

export class DatabaseStorage implements IStorage {
  // Organizations
  async getOrganization(id: string): Promise<Organization | undefined> {
    const [org] = await db.select().from(organizations).where(eq(organizations.id, id));
    return org;
  }

  async getOrganizationByEmail(email: string): Promise<Organization | undefined> {
    const [org] = await db.select().from(organizations).where(eq(organizations.email, email));
    return org;
  }

  async createOrganization(org: InsertOrganization): Promise<Organization> {
    const [newOrg] = await db.insert(organizations).values(org).returning();
    return newOrg;
  }

  async getOrganizations(): Promise<Organization[]> {
    return await db.select().from(organizations).orderBy(desc(organizations.createdAt));
  }

  async updateOrganization(id: string, data: Partial<InsertOrganization>): Promise<Organization> {
    const updateData = {
      ...data,
      updatedAt: new Date()
    };
    const [updatedOrg] = await db
      .update(organizations)
      .set(updateData)
      .where(eq(organizations.id, id))
      .returning();
    return updatedOrg;
  }

  async updateOrganizationPassword(email: string, hashedPassword: string): Promise<void> {
    await db
      .update(organizations)
      .set({ password: hashedPassword, updatedAt: new Date() })
      .where(eq(organizations.email, email));
  }

  // Admin methods
  async getAllOrganizationsForAdmin(): Promise<Organization[]> {
    return await db.select().from(organizations).orderBy(desc(organizations.createdAt));
  }

  async updateOrganizationStatus(id: string, isActive: boolean): Promise<Organization> {
    const [updatedOrg] = await db
      .update(organizations)
      .set({ isActive, updatedAt: new Date() })
      .where(eq(organizations.id, id))
      .returning();
    return updatedOrg;
  }

  async updateOrganizationFeatures(id: string, features: string[]): Promise<Organization> {
    const [updatedOrg] = await db
      .update(organizations)
      .set({ features, updatedAt: new Date() })
      .where(eq(organizations.id, id))
      .returning();
    return updatedOrg;
  }

  async deleteOrganization(id: string): Promise<void> {
    // Delete associated events first (cascade should handle this but let's be explicit)
    await db.delete(events).where(eq(events.organizationId, id));
    // Delete the organization
    await db.delete(organizations).where(eq(organizations.id, id));
  }

  // Events
  async getEvent(id: string): Promise<Event | undefined> {
    const [event] = await db.select().from(events).where(eq(events.id, id));
    return event;
  }

  async getEvents(): Promise<Event[]> {
    return await db.select().from(events).orderBy(desc(events.date));
  }

  async getEventsByOrganization(organizationId: string): Promise<Event[]> {
    return await db
      .select()
      .from(events)
      .where(eq(events.organizationId, organizationId))
      .orderBy(desc(events.date));
  }

  async createEvent(event: InsertEvent): Promise<Event> {
    // Vérifier les limites d'abonnement
    const organization = await this.getOrganization(event.organizationId);
    if (!organization) {
      throw new Error("Organisation non trouvée");
    }

    // Pour l'offre découverte, limiter à 1 événement
    if (organization.subscriptionType === 'decouverte') {
      const existingEvents = await db
        .select({ count: sql<number>`count(*)` })
        .from(events)
        .where(eq(events.organizationId, event.organizationId));

      if (existingEvents[0]?.count && existingEvents[0].count >= 1) {
        throw new Error("Limite d'événements atteinte pour l'offre Découverte (1 événement maximum). Passez à l'offre Premium pour créer plus d'événements.");
      }
    }

    const [newEvent] = await db.insert(events).values(event).returning();

    // Incrémenter le compteur d'événements créés
    await db
      .update(organizations)
      .set({ 
        eventCreatedCount: sql`${organizations.eventCreatedCount} + 1`,
        updatedAt: new Date()
      })
      .where(eq(organizations.id, event.organizationId));

    return newEvent;
  }

  async updateEvent(id: string, data: Partial<InsertEvent>): Promise<Event> {
    const [updatedEvent] = await db
      .update(events)
      .set({ ...data, updatedAt: new Date() })
      .where(eq(events.id, id))
      .returning();
    return updatedEvent;
  }

  async deleteEvent(id: string): Promise<void> {
    await db.delete(events).where(eq(events.id, id));
  }

  // Event Participants
  async getEventParticipant(id: string): Promise<EventParticipant | undefined> {
    const [participant] = await db
      .select()
      .from(eventParticipants)
      .where(eq(eventParticipants.id, id));
    return participant;
  }

  async getEventParticipants(eventId: string): Promise<EventParticipant[]> {
    return await db
      .select()
      .from(eventParticipants)
      .where(eq(eventParticipants.eventId, eventId))
      .orderBy(desc(eventParticipants.createdAt));
  }

  async addEventParticipant(participant: InsertEventParticipant): Promise<EventParticipant> {
    const [newParticipant] = await db.insert(eventParticipants).values(participant).returning();
    return newParticipant;
  }

  // Alias for backwards compatibility
  async createEventParticipant(participant: InsertEventParticipant): Promise<EventParticipant> {
    return this.addEventParticipant(participant);
  }

  async removeEventParticipant(id: string): Promise<void> {
    await db.delete(eventParticipants).where(eq(eventParticipants.id, id));
  }

  async updateEventParticipant(id: string, data: Partial<InsertEventParticipant>): Promise<EventParticipant> {
    const [updatedParticipant] = await db
      .update(eventParticipants)
      .set(data)
      .where(eq(eventParticipants.id, id))
      .returning();
    return updatedParticipant;
  }

  // Event Invitations
  async getEventInvitation(token: string): Promise<EventInvitation | undefined> {
    const [invitation] = await db
      .select()
      .from(eventInvitations)
      .where(eq(eventInvitations.token, token));
    return invitation;
  }

  async getEventInvitations(eventId: string): Promise<EventInvitation[]> {
    return await db
      .select()
      .from(eventInvitations)
      .where(eq(eventInvitations.eventId, eventId))
      .orderBy(desc(eventInvitations.sentAt));
  }

  async createEventInvitation(invitation: InsertEventInvitation): Promise<EventInvitation> {
    // Vérifier les limites d'invitations
    const event = await this.getEvent(invitation.eventId);
    if (!event) {
      throw new Error("Événement non trouvé");
    }

    const organization = await this.getOrganization(event.organizationId);
    if (!organization) {
      throw new Error("Organisation non trouvée");
    }

    // Pour l'offre découverte, limiter à 20 invitations par événement
    if (organization.subscriptionType === 'decouverte') {
      const existingInvitations = await db
        .select({ count: sql<number>`count(*)` })
        .from(eventInvitations)
        .where(eq(eventInvitations.eventId, invitation.eventId));

      if (existingInvitations[0]?.count && existingInvitations[0].count >= 20) {
        throw new Error("Limite d'invitations atteinte pour l'offre Découverte (20 invitations maximum par événement). Passez à l'offre Premium pour inviter plus de personnes.");
      }
    }

    const [newInvitation] = await db.insert(eventInvitations).values(invitation).returning();

    // Incrémenter le compteur d'invitations envoyées
    await db
      .update(organizations)
      .set({ 
        invitationsSentCount: sql`${organizations.invitationsSentCount} + 1`,
        updatedAt: new Date()
      })
      .where(eq(organizations.id, event.organizationId));

    return newInvitation;
  }

  async updateEventInvitation(id: string, data: Partial<InsertEventInvitation>): Promise<EventInvitation> {
    const [updatedInvitation] = await db
      .update(eventInvitations)
      .set({ ...data, respondedAt: new Date() })
      .where(eq(eventInvitations.id, id))
      .returning();
    return updatedInvitation;
  }

  // Messages
  async getMessage(id: string): Promise<Message | undefined> {
    const [message] = await db
      .select()
      .from(messages)
      .where(eq(messages.id, id));
    return message;
  }

  async getEventMessages(eventId: string): Promise<Message[]> {
    return await db
      .select()
      .from(messages)
      .where(eq(messages.eventId, eventId))
      .orderBy(desc(messages.createdAt));
  }

  async createMessage(message: InsertMessage): Promise<Message> {
    const [newMessage] = await db.insert(messages).values(message).returning();
    return newMessage;
  }

  async deleteMessage(id: string): Promise<void> {
    await db.delete(messages).where(eq(messages.id, id));
  }

  // Password Reset Tokens
  async createPasswordResetToken(token: InsertPasswordResetToken): Promise<PasswordResetToken> {
    const [newToken] = await db.insert(passwordResetTokens).values(token).returning();
    return newToken;
  }

  async getPasswordResetToken(token: string): Promise<PasswordResetToken | undefined> {
    const [resetToken] = await db
      .select()
      .from(passwordResetTokens)
      .where(and(
        eq(passwordResetTokens.token, token),
        isNull(passwordResetTokens.usedAt)
      ));
    return resetToken;
  }

  async markPasswordResetTokenAsUsed(id: string): Promise<void> {
    await db
      .update(passwordResetTokens)
      .set({ usedAt: new Date() })
      .where(eq(passwordResetTokens.id, id));
  }



  // Participant Change Requests
  async createParticipantChangeRequest(request: InsertParticipantChangeRequest): Promise<ParticipantChangeRequest> {
    const [newRequest] = await db
      .insert(participantChangeRequests)
      .values(request)
      .returning();
    return newRequest;
  }

  async getParticipantChangeRequestsByEvent(eventId: string): Promise<ParticipantChangeRequest[]> {
    return await db
      .select()
      .from(participantChangeRequests)
      .where(eq(participantChangeRequests.eventId, eventId))
      .orderBy(desc(participantChangeRequests.createdAt));
  }

  async getParticipantChangeRequest(id: string): Promise<ParticipantChangeRequest | undefined> {
    const [request] = await db
      .select()
      .from(participantChangeRequests)
      .where(eq(participantChangeRequests.id, id));
    return request;
  }

  async updateParticipantChangeRequest(id: string, data: Partial<InsertParticipantChangeRequest>): Promise<ParticipantChangeRequest> {
    const [updatedRequest] = await db
      .update(participantChangeRequests)
      .set(data)
      .where(eq(participantChangeRequests.id, id))
      .returning();
    return updatedRequest;
  }

  // Notifications
  async getNotificationsByOrganization(organizationId: string): Promise<Notification[]> {
    return await db
      .select()
      .from(notifications)
      .where(eq(notifications.organizationId, organizationId))
      .orderBy(desc(notifications.createdAt));
  }

  async createNotification(notification: InsertNotification): Promise<Notification> {
    const [newNotification] = await db
      .insert(notifications)
      .values(notification)
      .returning();
    return newNotification;
  }

  async markNotificationAsRead(id: string, organizationId: string): Promise<void> {
    await db
      .update(notifications)
      .set({ read: true })
      .where(and(
        eq(notifications.id, id),
        eq(notifications.organizationId, organizationId)
      ));
  }

  async markAllNotificationsAsRead(organizationId: string): Promise<void> {
    await db
      .update(notifications)
      .set({ read: true })
      .where(and(
        eq(notifications.organizationId, organizationId),
        eq(notifications.read, false)
      ));
  }

  async deleteNotification(id: string, organizationId: string): Promise<void> {
    await db
      .delete(notifications)
      .where(and(
        eq(notifications.id, id),
        eq(notifications.organizationId, organizationId)
      ));
  }

  // Email Reply Tokens
  async createEmailReplyToken(token: InsertEmailReplyToken): Promise<EmailReplyToken> {
    const [newToken] = await db.insert(emailReplyTokens).values(token).returning();
    return newToken;
  }

  async getEmailReplyToken(token: string): Promise<EmailReplyToken | undefined> {
    const [replyToken] = await db
      .select()
      .from(emailReplyTokens)
      .where(and(
        eq(emailReplyTokens.token, token),
        eq(emailReplyTokens.isActive, true)
      ));
    return replyToken;
  }

  async deactivateEmailReplyToken(token: string): Promise<void> {
    await db
      .update(emailReplyTokens)
      .set({ isActive: false })
      .where(eq(emailReplyTokens.token, token));
  }

  async getReplyTokenData(token: string): Promise<any> {
    const replyToken = await this.getEmailReplyToken(token);
    if (!replyToken) return null;

    return {
      eventId: replyToken.eventId,
      participantName: replyToken.participantName,
      participantEmail: replyToken.participantEmail,
      originalMessage: replyToken.originalMessage,
      originalMessageId: replyToken.originalMessageId,
      createdAt: replyToken.createdAt,
    };
  }

  async markReplyTokenAsUsed(token: string): Promise<void> {
    await db
      .update(emailReplyTokens)
      .set({ isActive: false })
      .where(eq(emailReplyTokens.token, token));
  }

  // Subscription limits methods
  async checkEventLimit(organizationId: string): Promise<{ canCreate: boolean; currentCount: number; limit?: number }> {
    const organization = await this.getOrganization(organizationId);
    if (!organization) {
      throw new Error("Organisation non trouvée");
    }

    const [result] = await db
      .select({ count: sql<number>`count(*)` })
      .from(events)
      .where(eq(events.organizationId, organizationId));

    const currentCount = result?.count || 0;

    if (organization.subscriptionType === 'decouverte') {
      return {
        canCreate: currentCount < 1,
        currentCount,
        limit: 1
      };
    }

    // Premium = illimité
    return {
      canCreate: true,
      currentCount,
      limit: undefined
    };
  }

  async checkInvitationLimit(eventId: string): Promise<{ canCreate: boolean; currentCount: number; limit?: number }> {
    const event = await this.getEvent(eventId);
    if (!event) {
      throw new Error("Événement non trouvé");
    }

    const organization = await this.getOrganization(event.organizationId);
    if (!organization) {
      throw new Error("Organisation non trouvée");
    }

    const [result] = await db
      .select({ count: sql<number>`count(*)` })
      .from(eventInvitations)
      .where(eq(eventInvitations.eventId, eventId));

    const currentCount = result?.count || 0;

    if (organization.subscriptionType === 'decouverte') {
      return {
        canCreate: currentCount < 20,
        currentCount,
        limit: 20
      };
    }

    // Premium = illimité
    return {
      canCreate: true,
      currentCount,
      limit: undefined
    };
  }

  async getSubscriptionLimits(organizationId: string): Promise<{ events: { current: number; limit?: number }; invitations: { current: number; limit?: number } }> {
    const organization = await this.getOrganization(organizationId);
    if (!organization) {
      throw new Error("Organisation non trouvée");
    }

    // Import new subscription config
    try {
      const { NEW_SUBSCRIPTION_LIMITS, validateSubscription, getDaysUntilExpiry } = await import('./subscription-config');

      const subscriptionType = organization.subscriptionType || 'decouverte';
      const limits = NEW_SUBSCRIPTION_LIMITS[subscriptionType];
      const validation = validateSubscription(organization);
      const daysUntilExpiry = getDaysUntilExpiry(organization);

      // Compter les événements
      const [eventsResult] = await db
        .select({ count: sql<number>`count(*)` })
        .from(events)
        .where(eq(events.organizationId, organizationId));

      const eventsCount = eventsResult?.count || 0;

      // Compter toutes les invitations
      const [invitationsResult] = await db
        .select({ count: sql<number>`count(*)` })
        .from(eventInvitations)
        .innerJoin(events, eq(eventInvitations.eventId, events.id))
        .where(eq(events.organizationId, organizationId));

      const invitationsCount = invitationsResult?.count || 0;

      return {
        events: { 
          current: eventsCount, 
          limit: limits.maxEvents,
          remaining: organization.packageRemainingEvents || null
        },
        invitations: { 
          current: invitationsCount, 
          limit: limits.maxInvitations 
        },
        validation: {
          isValid: validation.isValid,
          reason: validation.reason,
          needsRenewal: validation.needsRenewal,
          daysUntilExpiry
        }
      };
    } catch (error) {
      // Fallback to old system if new config fails to load
      const [eventsResult] = await db
        .select({ count: sql<number>`count(*)` })
        .from(events)
        .where(eq(events.organizationId, organizationId));

      const eventsCount = eventsResult?.count || 0;

      const [invitationsResult] = await db
        .select({ count: sql<number>`count(*)` })
        .from(eventInvitations)
        .innerJoin(events, eq(eventInvitations.eventId, events.id))
        .where(eq(events.organizationId, organizationId));

      const invitationsCount = invitationsResult?.count || 0;

      if (organization.subscriptionType === 'decouverte') {
        return {
          events: { current: eventsCount, limit: 1 },
          invitations: { current: invitationsCount, limit: 20 }
        };
      }

      return {
        events: { current: eventsCount, limit: undefined },
        invitations: { current: invitationsCount, limit: undefined }
      };
    }
  }

  // New methods for modernized subscription system
  async getAllOrganizations(): Promise<Organization[]> {
    return await db.select().from(organizations).orderBy(desc(organizations.createdAt));
  }

  async cleanupExpiredTokens(cutoffDate: Date): Promise<void> {
    // Clean up old password reset tokens
    await db.delete(passwordResetTokens)
      .where(sql`${passwordResetTokens.createdAt} < ${cutoffDate} OR ${passwordResetTokens.expiresAt} < ${new Date()}`);

    // Clean up old email reply tokens
    await db.delete(emailReplyTokens)
      .where(sql`${emailReplyTokens.createdAt} < ${cutoffDate} OR ${emailReplyTokens.expiresAt} < ${new Date()}`);
  }

  async cleanupOldNotifications(cutoffDate: Date): Promise<void> {
    // Clean up old read notifications
    await db.delete(notifications)
      .where(sql`${notifications.createdAt} < ${cutoffDate} AND ${notifications.read} = true`);
  }

  async saveMonthlyStats(stats: any): Promise<void> {
    // This is a simplified version - in production you'd save to monthly_statistics table
    console.log('📊 Monthly stats generated:', {
      period: stats.period,
      totalOrganizations: stats.totalOrganizations,
      activeSubscriptions: stats.activeSubscriptions,
      newSubscriptions: stats.newSubscriptions
    });
  }
}

export const storage = new DatabaseStorage();

