  --sidebar: hsl(180 6.6667% 97.0588%);
  --sidebar-foreground: hsl(210 25% 7.8431%);
  --sidebar-primary: hsl(217 91% 60%);
  --sidebar-primary-foreground: hsl(0 0% 100%);
  --sidebar-accent: hsl(211.5789 51.3514% 92.7451%);
  --sidebar-accent-foreground: hsl(217 91% 60%);
  --sidebar-border: hsl(205.0000 25.0000% 90.5882%);
  --sidebar-ring: hsl(217 91% 60%);
  --font-sans: 'Inter', system-ui, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: 0.5rem;
}

.dark {
  /* Ultra-modern dark theme with enhanced contrast and vibrancy */
  --background: hsl(220 26% 6%);
  --foreground: hsl(210 40% 98%);
  --card: hsl(222 24% 10%);
  --card-foreground: hsl(210 40% 96%);
  --popover: hsl(220 26% 7%);
  --popover-foreground: hsl(210 40% 98%);
  --primary: hsl(217 91% 60%);
  --primary-foreground: hsl(0 0% 100%);
  --secondary: hsl(217 38% 22%);
  --secondary-foreground: hsl(210 40% 98%);
  --muted: hsl(217 35% 18%);
  --muted-foreground: hsl(215 25% 70%);
  --accent: hsl(217 91% 60%);
  --accent-foreground: hsl(0 0% 100%);
  --destructive: hsl(0 84% 60%);
  --destructive-foreground: hsl(0 0% 100%);
  --border: hsl(217 35% 15%);
  --input: hsl(217 35% 15%);
  --ring: hsl(217 91% 60%);
  --sidebar: hsl(222 24% 9%);
  --sidebar-foreground: hsl(210 40% 96%);
  --sidebar-primary: hsl(217 91% 60%);
  --sidebar-primary-foreground: hsl(0 0% 100%);
  --sidebar-accent: hsl(217 38% 22%);
  --sidebar-accent-foreground: hsl(210 40% 98%);
  --sidebar-border: hsl(217 35% 15%);
  --sidebar-ring: hsl(217 91% 60%);

  /* Enhanced vibrant color palette for better UX */
  --success: hsl(142 76% 45%);
  --success-foreground: hsl(0 0% 100%);
  --warning: hsl(38 92% 55%);
  --warning-foreground: hsl(220 26% 6%);
  --info: hsl(199 89% 52%);
  --info-foreground: hsl(0 0% 100%);

  /* Premium gradient accents */
  --gradient-primary: linear-gradient(135deg, hsl(217 91% 60%) 0%, hsl(240 100% 70%) 100%);
  --gradient-success: linear-gradient(135deg, hsl(142 76% 45%) 0%, hsl(160 84% 39%) 100%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/* Custom animations */
@keyframes fadeIn {
  from { opacity: 0; transform: translateY(10px); }
  to { opacity: 1; transform: translateY(0); }
}

.animate-fade-in {
  animation: fadeIn 0.3s ease-out;
}

/* Hero gradient */
.hero-gradient {
  background: linear-gradient(135deg, hsl(217 91% 60%) 0%, hsl(160 84% 39%) 100%);
}

/* Glassmorphism effect */
.glass {
  backdrop-filter: blur(10px);
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

.dark .glass {
  background: rgba(17, 24, 39, 0.8);
  border: 1px solid rgba(55, 65, 81, 0.3);
}

/* Enhanced hover effects for better UX */
.dark .hover\:bg-gray-50:hover {
  background-color: hsl(217 32% 14%);
}

.dark .hover\:bg-gray-100:hover {
  background-color: hsl(217 32% 17%);
}

/* Improved focus rings for dark mode */
.dark .focus\:ring-2:focus {
  --tw-ring-color: hsl(217 91% 65%);
}

/* Better contrast for buttons in dark mode */
.dark .bg-primary\/10 {
  background-color: hsl(217 91% 65% / 0.15);
}

.dark .bg-secondary\/10 {
  background-color: hsl(160 84% 39% / 0.15);
}

.dark .bg-accent\/10 {
  background-color: hsl(0 84% 60% / 0.15);
}

/* Smooth transitions for theme switching */
* {
  transition: background-color 0.2s ease, border-color 0.2s ease, color 0.2s ease;
}

/* Enhanced card shadows for depth in dark mode */
.dark [class*="shadow"] {
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.3), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
}

/* Improved scrollbar styling */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: var(--muted);
}

::-webkit-scrollbar-thumb {
  background: var(--border);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--muted-foreground);
}

