import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface AdminRegistrationModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess?: () => void;
}

export default function AdminRegistrationModal({ 
  isOpen, 
  onClose, 
  onSuccess 
}: AdminRegistrationModalProps) {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
    firstName: "",
    lastName: "",
    secretKey: "",
  });
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.name || !formData.email || !formData.password || 
        !formData.firstName || !formData.lastName || !formData.secretKey) {
      toast({
        title: "Erreur",
        description: "Veuillez remplir tous les champs.",
        variant: "destructive",
      });
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      toast({
        title: "Erreur",
        description: "Les mots de passe ne correspondent pas.",
        variant: "destructive",
      });
      return;
    }

    if (formData.password.length < 8) {
      toast({
        title: "Erreur",
        description: "Le mot de passe doit contenir au moins 8 caractères.",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);

    try {
      const response = await apiRequest("POST", "/api/admin/register-super-user", {
        name: formData.name,
        email: formData.email,
        password: formData.password,
        firstName: formData.firstName,
        lastName: formData.lastName,
        secretKey: formData.secretKey,
      });

      const data = await response.json();

      toast({
        title: "Succès",
        description: "Administrateur créé avec succès !",
      });

      // Reset form
      setFormData({
        name: "",
        email: "",
        password: "",
        confirmPassword: "",
        firstName: "",
        lastName: "",
        secretKey: "",
      });

      onClose();
      if (onSuccess) {
        onSuccess();
      }

    } catch (error: any) {
      console.error("Admin registration error:", error);
      toast({
        title: "Erreur",
        description: error.message || "Échec de la création de l'administrateur.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleChange = (field: string) => (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData(prev => ({
      ...prev,
      [field]: e.target.value
    }));
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="text-2xl font-bold text-gray-900">
            🔐 Inscription Administrateur
          </DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="firstName">Prénom</Label>
              <Input 
                id="firstName"
                type="text"
                value={formData.firstName}
                onChange={handleChange('firstName')}
                placeholder="Jean"
                required
                disabled={isLoading}
              />
            </div>
            <div>
              <Label htmlFor="lastName">Nom</Label>
              <Input 
                id="lastName"
                type="text"
                value={formData.lastName}
                onChange={handleChange('lastName')}
                placeholder="Dupont"
                required
                disabled={isLoading}
              />
            </div>
          </div>

          <div>
            <Label htmlFor="name">Nom de l'organisation</Label>
            <Input 
              id="name"
              type="text"
              value={formData.name}
              onChange={handleChange('name')}
              placeholder="Administration TeamMove"
              required
              disabled={isLoading}
            />
          </div>

          <div>
            <Label htmlFor="email">Email</Label>
            <Input 
              id="email"
              type="email"
              value={formData.email}
              onChange={handleChange('email')}
              placeholder="admin@TeamMove.com"
              required
              disabled={isLoading}
            />
          </div>

          <div>
            <Label htmlFor="password">Mot de passe</Label>
            <Input 
              id="password"
              type="password"
              value={formData.password}
              onChange={handleChange('password')}
              placeholder="••••••••"
              required
              disabled={isLoading}
            />
          </div>

          <div>
            <Label htmlFor="confirmPassword">Confirmer le mot de passe</Label>
            <Input 
              id="confirmPassword"
              type="password"
              value={formData.confirmPassword}
              onChange={handleChange('confirmPassword')}
              placeholder="••••••••"
              required
              disabled={isLoading}
            />
          </div>

          <div>
            <Label htmlFor="secretKey">Clé secrète</Label>
            <Input 
              id="secretKey"
              type="password"
              value={formData.secretKey}
              onChange={handleChange('secretKey')}
              placeholder="Clé secrète administrateur"
              required
              disabled={isLoading}
            />
          </div>

          <div className="bg-yellow-50 border border-yellow-200 rounded-md p-3">
            <p className="text-yellow-800 text-sm">
              ⚠️ Cette fonctionnalité est réservée aux développeurs et administrateurs système.
            </p>
          </div>

          <Button 
            type="submit" 
            className="w-full bg-red-600 hover:bg-red-700"
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                Création...
              </>
            ) : (
              "Créer l'administrateur"
            )}
          </Button>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/admin/AdminSupport.content:
import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { Textarea } from '../ui/textarea';
import { Badge } from '../ui/badge';
import { 
  MessageSquare, 
  Plus, 
  Send, 
  Clock, 
  CheckCircle, 
  XCircle,
  AlertCircle,
  MessageCircle,
  User,
  Shield
} from 'lucide-react';
import { toast } from 'sonner';
import { formatDistanceToNow } from 'date-fns';
import { fr } from 'date-fns/locale';
import { api } from '@/lib/api';

interface Conversation {
  id: string;
  subject: string;
  status: 'open' | 'closed' | 'pending';
  priority: 'low' | 'medium' | 'high' | 'urgent';
  lastMessageAt: string;
  createdAt: string;
}

interface Message {
  id: string;
  senderId: string;
  senderType: 'admin' | 'organization';
  senderName: string;
  senderEmail: string;
  message: string;
  messageType: 'text' | 'system' | 'attachment';
  read: boolean;
  createdAt: string;
}

export function AdminSupport() {
  const [conversations, setConversations] = useState<Conversation[]>([]);
  const [selectedConversation, setSelectedConversation] = useState<string | null>(null);
  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [showNewConversation, setShowNewConversation] = useState(false);
  const [newConversationData, setNewConversationData] = useState({
    subject: '',
    message: '',
    priority: 'medium' as 'low' | 'medium' | 'high' | 'urgent'
  });
  const [loading, setLoading] = useState(true);
  const [sending, setSending] = useState(false);

  useEffect(() => {
    fetchConversations();
  }, []);

  useEffect(() => {
    if (selectedConversation) {
      fetchMessages(selectedConversation);
    }
  }, [selectedConversation]);

  const fetchConversations = async () => {
    try {
      const response = await api.admin.getConversations();

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Erreur lors du chargement des conversations');
      }

      const data = await response.json();
      setConversations(data);
    } catch (error: any) {
      console.error('Error fetching conversations:', error);
      toast.error(error.message || 'Erreur lors du chargement des conversations');
    } finally {
      setLoading(false);
    }
  };

  const fetchMessages = async (conversationId: string) => {
    try {
      const response = await api.admin.getMessages(conversationId);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Erreur lors du chargement des messages');
      }

      const data = await response.json();
      setMessages(data);
    } catch (error: any) {
      console.error('Error fetching messages:', error);
      toast.error(error.message || 'Erreur lors du chargement des messages');
    }
  };

  const createNewConversation = async () => {
    if (!newConversationData.subject.trim() || !newConversationData.message.trim()) {
      toast.error('Veuillez remplir tous les champs');
      return;
    }

    setSending(true);
    try {
      const response = await api.admin.createConversation(newConversationData);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Erreur lors de la création de la conversation');
      }

      const conversation = await response.json();
      setConversations(prev => [conversation, ...prev]);
      setSelectedConversation(conversation.id);
      setShowNewConversation(false);
      setNewConversationData({ subject: '', message: '', priority: 'medium' });
      toast.success('Conversation créée avec succès');
    } catch (error: any) {
      console.error('Error creating conversation:', error);
      toast.error(error.message || 'Erreur lors de la création de la conversation');
    } finally {
      setSending(false);
    }
  };

  const sendMessage = async () => {
    if (!newMessage.trim() || !selectedConversation) return;

    setSending(true);
    try {
      const response = await api.admin.sendMessage(selectedConversation, newMessage);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Erreur lors de l\'envoi du message');
      }

      const message = await response.json();
      setMessages(prev => [...prev, message]);
      setNewMessage('');
      // Mettre à jour la conversation dans la liste
      setConversations(prev => 
        prev.map(conv => 
          conv.id === selectedConversation 
            ? { ...conv, lastMessageAt: new Date().toISOString() }
            : conv
        )
      );
    } catch (error: any) {
      console.error('Error sending message:', error);
      toast.error(error.message || 'Erreur lors de l\'envoi du message');
    } finally {
      setSending(false);
    }
  };

  const closeConversation = async (conversationId: string) => {
    try {
      const response = await api.admin.closeConversation(conversationId);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Erreur lors de la fermeture de la conversation');
      }

      setConversations(prev => 
        prev.map(conv => 
          conv.id === conversationId 
            ? { ...conv, status: 'closed' as const }
            : conv
        )
      );
      toast.success('Conversation fermée');
    } catch (error: any) {
      console.error('Error closing conversation:', error);
      toast.error(error.message || 'Erreur lors de la fermeture de la conversation');
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'open':
        return <Badge variant="secondary" className="bg-green-100 text-green-800">Ouverte</Badge>;
      case 'pending':
        return <Badge variant="secondary" className="bg-yellow-100 text-yellow-800">En attente</Badge>;
      case 'closed':
        return <Badge variant="secondary" className="bg-gray-100 text-gray-800">Fermée</Badge>;
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  const getPriorityBadge = (priority: string) => {
    switch (priority) {
      case 'urgent':
        return <Badge variant="destructive">Urgent</Badge>;
      case 'high':
        return <Badge variant="secondary" className="bg-red-100 text-red-800">Haute</Badge>;
      case 'medium':
        return <Badge variant="secondary" className="bg-blue-100 text-blue-800">Moyenne</Badge>;
      case 'low':
        return <Badge variant="secondary" className="bg-gray-100 text-gray-800">Basse</Badge>;
      default:
        return <Badge variant="secondary">{priority}</Badge>;
    }
  };

  const selectedConv = conversations.find(c => c.id === selectedConversation);

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 h-[600px]">
      {/* Conversations List */}
      <Card className="lg:col-span-1">
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <MessageSquare className="w-5 h-5" />
              Support
            </CardTitle>
            <Button
              size="sm"
              onClick={() => setShowNewConversation(true)}
            >
              <Plus className="w-4 h-4" />
            </Button>
          </div>
        </CardHeader>
        <CardContent className="p-0">
          {loading ? (
            <div className="space-y-3 p-4">
              {Array.from({ length: 3 }).map((_, i) => (
                <div key={i} className="animate-pulse">
                  <div className="h-16 bg-gray-200 rounded"></div>
                </div>
              ))}
            </div>
          ) : (
            <div className="divide-y max-h-96 overflow-y-auto">
              {conversations.map((conversation) => (
                <div
                  key={conversation.id}
                  onClick={() => setSelectedConversation(conversation.id)}
                  className={`p-4 cursor-pointer hover:bg-gray-50 transition-colors ${
                    selectedConversation === conversation.id ? 'bg-blue-50 border-r-2 border-r-blue-500' : ''
                  }`}
                >
                  <div className="flex items-start justify-between">
                    <div className="flex-1 min-w-0">
                      <h4 className="text-sm font-medium truncate">
                        {conversation.subject}
                      </h4>
                      <div className="flex items-center gap-2 mt-1">
                        {getStatusBadge(conversation.status)}
                        {getPriorityBadge(conversation.priority)}
                      </div>
                      <p className="text-xs text-gray-500 mt-2">
                        {formatDistanceToNow(new Date(conversation.lastMessageAt), { 
                          addSuffix: true, 
                          locale: fr 
                        })}
                      </p>
                    </div>
                  </div>
                </div>
              ))}

              {conversations.length === 0 && (
                <div className="p-4 text-center text-gray-500">
                  <MessageCircle className="w-8 h-8 mx-auto mb-2" />
                  <p className="text-sm">Aucune conversation</p>
                </div>
              )}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Messages Area */}
      <Card className="lg:col-span-2">
        {showNewConversation ? (
          <div>
            <CardHeader>
              <CardTitle>Nouvelle conversation</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <Input
                placeholder="Sujet de la conversation"
                value={newConversationData.subject}
                onChange={(e) => setNewConversationData(prev => ({ ...prev, subject: e.target.value }))}
              />

              <div className="space-y-2">
                <label className="text-sm font-medium">Priorité</label>
                <select 
                  className="w-full p-2 border rounded-md"
                  value={newConversationData.priority}
                  onChange={(e) => setNewConversationData(prev => ({ 
                    ...prev, 
                    priority: e.target.value as 'low' | 'medium' | 'high' | 'urgent'
                  }))}
                >
                  <option value="low">Basse</option>
                  <option value="medium">Moyenne</option>
                  <option value="high">Haute</option>
                  <option value="urgent">Urgente</option>
                </select>
              </div>

              <Textarea
                placeholder="Décrivez votre demande..."
                rows={6}
                value={newConversationData.message}
                onChange={(e) => setNewConversationData(prev => ({ ...prev, message: e.target.value }))}
              />

              <div className="flex gap-3">
                <Button 
                  onClick={createNewConversation}
                  disabled={sending}
                  className="flex-1"
                >
                  <Send className="w-4 h-4 mr-2" />
                  Envoyer
                </Button>
                <Button 
                  variant="outline" 
                  onClick={() => setShowNewConversation(false)}
                  className="flex-1"
                >
                  Annuler
                </Button>
              </div>
            </CardContent>
          </div>
        ) : selectedConv ? (
          <div className="flex flex-col h-full">
            <CardHeader className="pb-3">
              <div className="flex items-center justify-between">
                <div>
                  <CardTitle className="text-lg">{selectedConv.subject}</CardTitle>
                  <div className="flex items-center gap-2 mt-1">
                    {getStatusBadge(selectedConv.status)}
                    {getPriorityBadge(selectedConv.priority)}
                  </div>
                </div>
                {selectedConv.status !== 'closed' && (
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => closeConversation(selectedConv.id)}
                  >
                    <XCircle className="w-4 h-4 mr-1" />
                    Fermer
                  </Button>
                )}
              </div>
            </CardHeader>

            {/* Messages */}
            <CardContent className="flex-1 flex flex-col min-h-0">
              <div className="flex-1 overflow-y-auto space-y-4 mb-4">
                {messages.map((message) => (
                  <div
                    key={message.id}
                    className={`flex gap-3 ${
                      message.senderType === 'organization' ? 'justify-end' : 'justify-start'
                    }`}
                  >
                    <div
                      className={`max-w-[70%] rounded-lg p-3 ${
                        message.senderType === 'organization'
                          ? 'bg-blue-500 text-white'
                          : message.messageType === 'system'
                          ? 'bg-gray-100 text-gray-600 text-center italic'
                          : 'bg-gray-100 text-gray-900'
                      }`}
                    >
                      {message.messageType !== 'system' && (
                        <div className="flex items-center gap-2 mb-1">
                          {message.senderType === 'admin' ? (
                            <Shield className="w-4 h-4" />
                          ) : (
                            <User className="w-4 h-4" />
                          )}
                          <span className="text-xs font-medium">
                            {message.senderType === 'admin' ? 'Administration' : 'Vous'}
                          </span>
                        </div>
                      )}
                      <p className="text-sm whitespace-pre-wrap">{message.message}</p>
                      <p className={`text-xs mt-2 ${
                        message.senderType === 'organization' ? 'text-blue-100' : 'text-gray-500'
                      }`}>
                        {formatDistanceToNow(new Date(message.createdAt), { 
                          addSuffix: true, 
                          locale: fr 
                        })}
                      </p>
                    </div>
                  </div>
                ))}
              </div>

              {/* Message Input */}
              {selectedConv.status !== 'closed' && (
                <div className="flex gap-2">
                  <Textarea
                    placeholder="Tapez votre message..."
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    rows={2}
                    className="flex-1"
                    onKeyDown={(e) => {
                      if (e.key === 'Enter' && !e.shiftKey) {
                        e.preventDefault();
                        sendMessage();
                      }
                    }}
                  />
                  <Button
                    onClick={sendMessage}
                    disabled={!newMessage.trim() || sending}
                    className="self-end"
                  >
                    <Send className="w-4 h-4" />
                  </Button>
                </div>
              )}
            </CardContent>
          </div>
        ) : (
          <CardContent className="flex items-center justify-center h-full text-gray-500">
            <div className="text-center">
              <MessageSquare className="w-12 h-12 mx-auto mb-4" />
              <p>Sélectionnez une conversation ou créez-en une nouvelle</p>
            </div>
          </CardContent>
        )}
      </Card>
    </div>
  );
}

client/src/components/admin/Organization.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Switch } from "@/components/ui/switch";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface Organization {
  id: string;
  name: string;
  email: string;
  type: string;
  contactFirstName: string;
  contactLastName: string;
  logoUrl?: string;
  isActive: boolean;
  features: string[];
  createdAt: string;
}

const AVAILABLE_FEATURES = [
  { id: "messaging", name: "Messagerie avancée", description: "Communication avec participants et notifications" },
  { id: "analytics", name: "Analytics", description: "Statistiques détaillées et rapports avancés" },
  { id: "custom_branding", name: "Personnalisation", description: "Logo et couleurs personnalisés" },
  { id: "bulk_invite", name: "Invitations en masse", description: "Inviter plusieurs participants simultanément" },
  { id: "event_templates", name: "Modèles d'événements", description: "Réutiliser des configurations d'événements" },
  { id: "priority_support", name: "Support prioritaire", description: "Assistance dédiée et réponse rapide" },
  { id: "csv_import", name: "Import CSV/Excel", description: "Importer des listes d'emails depuis des fichiers" },
  { id: "advanced_stats", name: "Statistiques avancées", description: "Analyses poussées et tableaux de bord détaillés" },
];

export default function OrganizationsManagement() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedOrg, setSelectedOrg] = useState<Organization | null>(null);
  const [isFeatureModalOpen, setIsFeatureModalOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: organizations = [], isLoading, error } = useQuery<Organization[]>({
    queryKey: ["/api/admin/organizations"],
  });

  const updateStatusMutation = useMutation({
    mutationFn: async ({ id, isActive }: { id: string; isActive: boolean }) => {
      return await apiRequest("PATCH", `/api/admin/organizations/${id}/status`, { isActive });
    },
    onSuccess: (_, { isActive }) => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/organizations"] });
      toast({
        title: "Statut mis à jour",
        description: `L'organisation a été ${isActive ? 'activée' : 'désactivée'} avec succès.`,
      });
    },
    onError: (error: any) => {
      toast({
        title: "Erreur",
        description: error?.message || "Impossible de mettre à jour le statut.",
        variant: "destructive",
      });
    },
  });

  const updateFeaturesMutation = useMutation({
    mutationFn: async ({ id, features }: { id: string; features: string[] }) => {
      return await apiRequest("PATCH", `/api/admin/organizations/${id}/features`, { features });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/organizations"] });
      setIsFeatureModalOpen(false);
      setSelectedOrg(null);
      toast({
        title: "Fonctionnalités mises à jour",
        description: "Les fonctionnalités ont été modifiées avec succès.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Erreur",
        description: error?.message || "Impossible de mettre à jour les fonctionnalités.",
        variant: "destructive",
      });
    },
  });

  const deleteOrgMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/admin/organizations/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/organizations"] });
      toast({
        title: "Organisation supprimée",
        description: "L'organisation a été supprimée avec succès.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Erreur",
        description: error?.message || "Impossible de supprimer l'organisation.",
        variant: "destructive",
      });
    },
  });

  const filteredOrganizations = organizations.filter(org =>
    org.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    org.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
    `${org.contactFirstName} ${org.contactLastName}`.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleStatusToggle = (org: Organization) => {
    if (confirm(`Êtes-vous sûr de vouloir ${org.isActive ? 'désactiver' : 'activer'} l'organisation "${org.name}" ?`)) {
      updateStatusMutation.mutate({ id: org.id, isActive: !org.isActive });
    }
  };

  const handleDeleteOrg = (org: Organization) => {
    if (confirm(`Êtes-vous sûr de vouloir supprimer définitivement l'organisation "${org.name}" ? Cette action est irréversible.`)) {
      deleteOrgMutation.mutate(org.id);
    }
  };

  const handleUpdateFeatures = (features: string[]) => {
    if (selectedOrg) {
      updateFeaturesMutation.mutate({ id: selectedOrg.id, features });
    }
  };

  if (isLoading) {
    return (
      <div className="animate-fade-in">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Gestion des Organisations
          </h1>
          <p className="text-gray-600">Gérez les organisations inscrites sur la plateforme.</p>
        </div>

        <Card>
          <CardContent className="p-6">
            <div className="animate-pulse space-y-4">
              {[...Array(5)].map((_, i) => (
                <div key={i} className="flex items-center space-x-4">
                  <div className="w-12 h-12 bg-gray-200 rounded-full"></div>
                  <div className="flex-1">
                    <div className="h-4 bg-gray-200 rounded mb-2"></div>
                    <div className="h-3 bg-gray-200 rounded w-1/2"></div>
                  </div>
                  <div className="w-24 h-8 bg-gray-200 rounded"></div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (error) {
    return (
      <div className="animate-fade-in">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Gestion des Organisations
          </h1>
          <p className="text-gray-600">Gérez les organisations inscrites sur la plateforme.</p>
        </div>

        <Card>
          <CardContent className="p-6 text-center">
            <i className="fas fa-exclamation-triangle text-red-500 text-4xl mb-4"></i>
            <p className="text-gray-600">Erreur lors du chargement des organisations</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="animate-fade-in">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">
          Gestion des Organisations
        </h1>
        <p className="text-gray-600">
          Gérez les organisations inscrites sur la plateforme ({organizations.length} organisation{organizations.length !== 1 ? 's' : ''}).
        </p>
      </div>

      {/* Search and Filters */}
      <Card className="mb-6">
        <CardContent className="p-6">
          <div className="flex items-center space-x-4">
            <div className="flex-1 relative">
              <i className="fas fa-search absolute left-3 top-3 text-gray-400"></i>
              <Input
                placeholder="Rechercher par nom, email ou contact..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
              />
            </div>
            <Button variant="outline">
              <i className="fas fa-filter mr-2"></i>
              Filtres
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Organizations List */}
      <Card>
        <CardHeader>
          <CardTitle>
            Organisations ({filteredOrganizations.length})
          </CardTitle>
        </CardHeader>
        <CardContent className="p-0">
          {filteredOrganizations.length === 0 ? (
            <div className="p-8 text-center">
              <i className="fas fa-search text-gray-400 text-4xl mb-4"></i>
              <p className="text-gray-600">Aucune organisation trouvée</p>
              {searchTerm && (
                <p className="text-gray-500 text-sm">
                  Essayez de modifier votre recherche
                </p>
              )}
            </div>
          ) : (
            <div className="divide-y divide-gray-200">
              {filteredOrganizations.map((org) => (
                <div key={org.id} className="p-6 hover:bg-gray-50 transition-colors">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      <Avatar className="w-12 h-12">
                        <AvatarImage src={org.logoUrl} alt={org.name} />
                        <AvatarFallback>
                          {org.name.slice(0, 2).toUpperCase()}
                        </AvatarFallback>
                      </Avatar>

                      <div>
                        <div className="flex items-center space-x-2">
                          <h3 className="font-semibold text-gray-900">{org.name}</h3>
                          <Badge variant={org.isActive ? "default" : "secondary"}>
                            {org.isActive ? "Actif" : "Inactif"}
                          </Badge>
                          <Badge variant="outline">
                            {org.type}
                          </Badge>
                        </div>
                        <p className="text-gray-600 text-sm">
                          {org.contactFirstName} {org.contactLastName} • {org.email}
                        </p>
                        <p className="text-gray-500 text-xs">
                          Inscrit le {new Date(org.createdAt).toLocaleDateString('fr-FR')}
                        </p>
                        {org.features.length > 0 && (
                          <div className="flex flex-wrap gap-1 mt-2">
                            {org.features.slice(0, 3).map((feature) => {
                              const featureInfo = AVAILABLE_FEATURES.find(f => f.id === feature);
                              return (
                                <Badge key={feature} variant="outline" className="text-xs">
                                  {featureInfo?.name || feature}
                                </Badge>
                              );
                            })}
                            {org.features.length > 3 && (
                              <Badge variant="outline" className="text-xs">
                                +{org.features.length - 3} autres
                              </Badge>
                            )}
                          </div>
                        )}
                      </div>
                    </div>

                    <div className="flex items-center space-x-4">
                      {/* Status Toggle */}
                      <div className="flex items-center space-x-2">
                        <span className="text-sm text-gray-600">
                          {org.isActive ? "Actif" : "Inactif"}
                        </span>
                        <Switch
                          checked={org.isActive}
                          onCheckedChange={() => handleStatusToggle(org)}
                          disabled={updateStatusMutation.isPending}
                        />
                      </div>

                      {/* Actions */}
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => {
                          setSelectedOrg(org);
                          setIsFeatureModalOpen(true);
                        }}
                      >
                        <i className="fas fa-cog mr-2"></i>
                        Fonctionnalités
                      </Button>

                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleDeleteOrg(org)}
                        disabled={deleteOrgMutation.isPending}
                        className="text-red-600 hover:text-red-700 hover:border-red-300"
                      >
                        <i className="fas fa-trash mr-2"></i>
                        Supprimer
                      </Button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Features Modal */}
      {isFeatureModalOpen && selectedOrg && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-2xl max-h-[80vh] overflow-y-auto">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-bold text-gray-900">
                Fonctionnalités - {selectedOrg.name}
              </h2>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setIsFeatureModalOpen(false)}
              >
                <i className="fas fa-times"></i>
              </Button>
            </div>

            <div className="space-y-4">
              {AVAILABLE_FEATURES.map((feature) => {
                const isEnabled = selectedOrg.features.includes(feature.id);

                return (
                  <div key={feature.id} className="flex items-center justify-between p-4 border border-gray-200 rounded-lg">
                    <div className="flex-1">
                      <h3 className="font-medium text-gray-900">{feature.name}</h3>
                      <p className="text-sm text-gray-600">{feature.description}</p>
                    </div>

                    <Switch
                      checked={isEnabled}
                      onCheckedChange={(checked) => {
                        const newFeatures = checked
                          ? [...selectedOrg.features, feature.id]
                          : selectedOrg.features.filter(f => f !== feature.id);
                        handleUpdateFeatures(newFeatures);
                      }}
                      disabled={updateFeaturesMutation.isPending}
                    />
                  </div>
                );
              })}
            </div>

            <div className="mt-6 flex justify-end space-x-3">
              <Button
                variant="outline"
                onClick={() => setIsFeatureModalOpen(false)}
              >
                Fermer
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/captcha/ModernCapt.content:
import React, { useState, useEffect, useCallback } from 'react';
import { Button } from '@/components/ui/button';

interface ModernCaptchaProps {
  onVerify: (isVerified: boolean) => void;
  className?: string;
}

type CaptchaType = 'math' | 'image' | 'pattern';

interface MathChallenge {
  question: string;
  answer: number;
}

interface ImageChallenge {
  question: string;
  images: { id: string; src: string; isCorrect: boolean }[];
  correctCount: number;
}

const ModernCaptcha: React.FC<ModernCaptchaProps> = ({ onVerify, className = '' }) => {
  const [isVerified, setIsVerified] = useState(false);
  const [currentType, setCurrentType] = useState<CaptchaType>('math');
  const [mathChallenge, setMathChallenge] = useState<MathChallenge | null>(null);
  const [imageChallenge, setImageChallenge] = useState<ImageChallenge | null>(null);
  const [userAnswer, setUserAnswer] = useState('');
  const [selectedImages, setSelectedImages] = useState<string[]>([]);
  const [attempts, setAttempts] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);

  // Generate math challenge
  const generateMathChallenge = useCallback((): MathChallenge => {
    const operations = ['+', '-', '*'];
    const operation = operations[Math.floor(Math.random() * operations.length)];

    let num1: number, num2: number, answer: number, question: string;

    switch (operation) {
      case '+':
        num1 = Math.floor(Math.random() * 20) + 1;
        num2 = Math.floor(Math.random() * 20) + 1;
        answer = num1 + num2;
        question = `${num1} + ${num2}`;
        break;
      case '-':
        num1 = Math.floor(Math.random() * 30) + 10;
        num2 = Math.floor(Math.random() * num1) + 1;
        answer = num1 - num2;
        question = `${num1} - ${num2}`;
        break;
      case '*':
        num1 = Math.floor(Math.random() * 12) + 1;
        num2 = Math.floor(Math.random() * 12) + 1;
        answer = num1 * num2;
        question = `${num1} × ${num2}`;
        break;
      default:
        num1 = 5;
        num2 = 3;
        answer = 8;
        question = `${num1} + ${num2}`;
    }

    return { question, answer };
  }, []);

  // Generate image challenge
  const generateImageChallenge = useCallback((): ImageChallenge => {
    const categories = [
      { name: 'véhicules', correct: ['🚗', '🚌', '🚛', '🏍️'], incorrect: ['🏠', '🌳', '📱', '⚽'] },
      { name: 'sports', correct: ['⚽', '🏀', '🎾', '🏐'], incorrect: ['🍎', '🌙', '📚', '🎵'] },
      { name: 'animaux', correct: ['🐶', '🐱', '🐭', '🐰'], incorrect: ['🌸', '🍕', '💻', '🎭'] }
    ];

    const category = categories[Math.floor(Math.random() * categories.length)];
    const correctImages = [...category.correct].sort(() => Math.random() - 0.5).slice(0, 3);
    const incorrectImages = [...category.incorrect].sort(() => Math.random() - 0.5).slice(0, 5);

    const allImages = [...correctImages, ...incorrectImages]
      .map((emoji, index) => ({
        id: `img-${index}`,
        src: emoji,
        isCorrect: correctImages.includes(emoji)
      }))
      .sort(() => Math.random() - 0.5);

    return {
      question: `Sélectionnez tous les ${category.name}`,
      images: allImages,
      correctCount: correctImages.length
    };
  }, []);

  // Initialize challenge
  useEffect(() => {
    if (currentType === 'math') {
      setMathChallenge(generateMathChallenge());
    } else if (currentType === 'image') {
      setImageChallenge(generateImageChallenge());
    }
    setUserAnswer('');
    setSelectedImages([]);
  }, [currentType, generateMathChallenge, generateImageChallenge]);

  // Handle math verification
  const verifyMathAnswer = () => {
    if (!mathChallenge) return false;
    return parseInt(userAnswer) === mathChallenge.answer;
  };

  // Handle image verification
  const verifyImageAnswer = () => {
    if (!imageChallenge) return false;
    const correctImages = imageChallenge.images
      .filter(img => img.isCorrect)
      .map(img => img.id);

    return selectedImages.length === correctImages.length &&
           selectedImages.every(id => correctImages.includes(id));
  };

  // Handle submission
  const handleSubmit = async () => {
    setIsLoading(true);

    // Simulate processing time
    await new Promise(resolve => setTimeout(resolve, 1000));

    let isCorrect = false;

    if (currentType === 'math') {
      isCorrect = verifyMathAnswer();
    } else if (currentType === 'image') {
      isCorrect = verifyImageAnswer();
    }

    if (isCorrect) {
      setIsVerified(true);
      setShowSuccess(true);
      onVerify(true);

      // Hide success message after 2 seconds
      setTimeout(() => {
        setShowSuccess(false);
      }, 2000);
    } else {
      setAttempts(prev => prev + 1);

      // Switch challenge type after failed attempts
      if (attempts >= 2) {
        setCurrentType(prev => prev === 'math' ? 'image' : 'math');
        setAttempts(0);
      } else {
        // Generate new challenge of same type
        if (currentType === 'math') {
          setMathChallenge(generateMathChallenge());
        } else {
          setImageChallenge(generateImageChallenge());
        }
      }

      setUserAnswer('');
      setSelectedImages([]);
    }

    setIsLoading(false);
  };

  // Handle image selection
  const toggleImageSelection = (imageId: string) => {
    setSelectedImages(prev => 
      prev.includes(imageId) 
        ? prev.filter(id => id !== imageId)
        : [...prev, imageId]
    );
  };

  // Reset captcha
  const resetCaptcha = () => {
    setIsVerified(false);
    setShowSuccess(false);
    setAttempts(0);
    setCurrentType('math');
    onVerify(false);
  };

  if (isVerified && showSuccess) {
    return (
      <div className={`bg-green-50 border border-green-200 rounded-lg p-4 text-center ${className}`}>
        <div className="flex items-center justify-center space-x-2">
          <div className="w-6 h-6 bg-green-500 rounded-full flex items-center justify-center">
            <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
            </svg>
          </div>
          <span className="text-green-800 font-medium">✅ Vérification réussie</span>
        </div>
      </div>
    );
  }

  if (isVerified) {
    return (
      <div className={`bg-green-50 border border-green-200 rounded-lg p-3 text-center ${className}`}>
        <div className="flex items-center justify-between">
          <span className="text-green-800 text-sm">✅ Vérifié</span>
          <Button 
            variant="ghost" 
            size="sm" 
            onClick={resetCaptcha}
            className="text-green-600 hover:text-green-800 p-1 h-auto"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
            </svg>
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className={`bg-gray-50 border border-gray-200 rounded-lg p-4 ${className}`}>
      <div className="flex items-center space-x-2 mb-3">
        <div className="w-6 h-6 bg-blue-500 rounded-full flex items-center justify-center">
          <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
        </div>
        <span className="text-gray-800 font-medium">Vérification humaine</span>
        <div className="ml-auto text-xs text-gray-500">
          {attempts > 0 && `Tentative ${attempts + 1}`}
        </div>
      </div>

      {currentType === 'math' && mathChallenge && (
        <div className="space-y-3">
          <div className="text-center">
            <p className="text-gray-700 mb-2">Résolvez cette équation :</p>
            <div className="text-2xl font-mono font-bold text-blue-600 bg-white rounded-lg py-3 px-4 border">
              {mathChallenge.question} = ?
            </div>
          </div>

          <input
            type="number"
            value={userAnswer}
            onChange={(e) => setUserAnswer(e.target.value)}
            placeholder="Votre réponse"
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            disabled={isLoading}
          />
        </div>
      )}

      {currentType === 'image' && imageChallenge && (
        <div className="space-y-3">
          <p className="text-gray-700 text-center">{imageChallenge.question}</p>

          <div className="grid grid-cols-4 gap-2">
            {imageChallenge.images.map((image) => (
              <button
                key={image.id}
                onClick={() => toggleImageSelection(image.id)}
                disabled={isLoading}
                className={`aspect-square rounded-lg border-2 text-2xl flex items-center justify-center transition-all ${
                  selectedImages.includes(image.id)
                    ? 'border-blue-500 bg-blue-50'
                    : 'border-gray-300 bg-white hover:border-gray-400'
                }`}
              >
                {image.src}
              </button>
            ))}
          </div>

          <p className="text-xs text-gray-500 text-center">
            Cliquez sur toutes les images correspondantes
          </p>
        </div>
      )}

      <Button
        onClick={handleSubmit}
        disabled={isLoading || (currentType === 'math' && !userAnswer) || (currentType === 'image' && selectedImages.length === 0)}
        className="w-full mt-4 bg-blue-500 hover:bg-blue-600"
      >
        {isLoading ? (
          <div className="flex items-center space-x-2">
            <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
            <span>Vérification...</span>
          </div>
        ) : (
          'Vérifier'
        )}
      </Button>

      {attempts > 0 && (
        <p className="text-xs text-amber-600 text-center mt-2">
          ⚠️ Réponse incorrecte. {3 - attempts} tentative(s) restante(s)
        </p>
      )}
    </div>
  );
};

export default ModernCaptcha;

client/src/components/chatbot/Chatbot.ts.content:
import { useState, useEffect, useRef } from "react";

type Message = {
  id: number;
  role: "user" | "bot";
  text: string;
};

export default function Chatbot() {
  const [open, setOpen] = useState<boolean>(false);
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState<string>("");
  const inputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (open) inputRef.current?.focus();
  }, [open]);

  async function sendMessage() {
    if (!input.trim()) return;
    const userMsg: Message = { id: Date.now(), role: "user", text: input.trim() };
    setMessages((m) => [...m, userMsg]);
    setInput("");

    // Add loading message
    const loadingMsg: Message = { id: Date.now() + 1, role: "bot", text: "..." };
    setMessages((m) => [...m, loadingMsg]);

    try {
      // Get conversation history for context
      const conversationHistory = messages.map(msg => ({
        role: msg.role === "bot" ? "assistant" : "user",
        content: msg.text
      }));

      const res = await fetch("/api/chatbot/message", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ 
          message: userMsg.text,
          conversationHistory 
        }),
      });

      const data = await res.json();

      // Remove loading message and add bot response
      setMessages((m) => {
        const withoutLoading = m.filter(msg => msg.id !== loadingMsg.id);
        const botMsg: Message = { 
          id: Date.now() + 2, 
          role: "bot", 
          text: data.success ? data.message : "Désolé, je rencontre des difficultés. Veuillez réessayer." 
        };
        return [...withoutLoading, botMsg];
      });
    } catch (error) {
      // Remove loading message and add error
      setMessages((m) => {
        const withoutLoading = m.filter(msg => msg.id !== loadingMsg.id);
        const errorMsg: Message = { 
          id: Date.now() + 2, 
          role: "bot", 
          text: "Désolé, le service est temporairement indisponible. Veuillez réessayer plus tard." 
        };
        return [...withoutLoading, errorMsg];
      });
    }
  }

  const messagesEndRef = useRef<HTMLDivElement>(null);
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, open]);

  // Animation style for the speech bubble (fade in/out + floating)
  const bubbleAnimationStyle: React.CSSProperties = {
    animation: "bubbleFadeFloat 4s ease-in-out infinite",
    position: "absolute",
    bottom: "60px",
    left: "-170px",
    width: "160px",
    padding: "10px",
    backgroundColor: "#0ea5e9",
    color: "#fff",
    borderRadius: "15px",
    fontSize: "0.85rem",
    fontWeight: "500",
    boxShadow: "0 4px 10px rgba(14, 165, 233, 0.4)",
    userSelect: "none",
  };

  return (
    <>
      <style>
        {`
          @keyframes bubbleFadeFloat {
            0%, 100% { opacity: 0; transform: translateY(4px); }
            50% { opacity: 1; transform: translateY(0); }
          }
          /* Clignotement des yeux */
          .eye-animate ellipse {
            animation: blink 2.5s infinite;
            transform-origin: center;
          }
          .eye-animate ellipse:nth-child(1) {
            animation-delay: 0s;
          }
          .eye-animate ellipse:nth-child(2) {
            animation-delay: 0.25s;
          }
          @keyframes blink {
            0%, 100% { ry: 3; }
            50% { ry: 0.7; }
          }
        `}
      </style>

      <div className="fixed bottom-6 right-6 z-50 flex flex-col items-end">
        {open && (
          <div className="w-80 max-w-xs bg-white rounded-xl shadow-xl flex flex-col p-0 overflow-hidden">
            <div className="flex items-center justify-between p-4 border-b bg-primary text-white rounded-t-xl">
              <strong>Assistant TeamMove</strong>
              <button
                onClick={() => setOpen(false)}
                aria-label="Fermer le chat"
                className="text-xl hover:bg-primary/20 p-1 rounded"
              >
                ×
              </button>
            </div>
            <div className="flex-1 h-64 overflow-y-auto px-2 py-3 bg-gray-50 custom-scrollbar" data-testid="chat-messages">
              {messages.length === 0 && (
                <div className="text-sm text-gray-500 text-center">
                  Bonjour ! Posez-moi une question sur le site.
                </div>
              )}
              {messages.map((m) => (
                <div
                  key={m.id}
                  className={`flex ${
                    m.role === "user" ? "justify-end" : "justify-start"
                  } mb-2`}
                >
                  <div
                    className={`message
                  px-3 py-2 rounded-2xl max-w-[75%] whitespace-pre-line break-words  
                  ${
                    m.role === "user"
                      ? "bg-primary text-white rounded-br-sm"
                      : "bg-gray-200 text-gray-900 rounded-bl-sm"
                  }
                  shadow-sm`}
                  >
                    {m.text}
                  </div>
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>
            <form
              className="flex items-center gap-2 bg-white px-3 py-2 border-t"
              onSubmit={(e) => {
                e.preventDefault();
                sendMessage();
              }}
            >
              <input
                ref={inputRef}
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === "Enter" && !e.shiftKey) {
                    e.preventDefault();
                    sendMessage();
                  }
                }}
                className="flex-1 rounded-full border border-gray-300 bg-gray-100 px-4 py-2 focus:outline-none focus:ring focus:ring-primary/30 transition"
                aria-label="Message"
                placeholder="Écrire un message…"
                autoComplete="off"
                data-testid="chat-input"
              />
              <button
                type="submit"
                className="w-10 h-10 bg-primary hover:bg-primary/90 rounded-full p-0 flex items-center justify-center text-white disabled:opacity-50 transition"
                disabled={!input.trim()}
                aria-label="Envoyer"
                data-testid="send-button"
              >
                <svg viewBox="0 0 24 24" width={22} height={22} fill="currentColor">
                  <path d="M2 21l21-9-21-9v7l15 2-15 2z" />
                </svg>
              </button>
            </form>
          </div>
        )}

        {/* Bouton flottant avec bulle d'idée animée */}
        <div className="relative flex items-center justify-center">
          { !open && (
            <div style={bubbleAnimationStyle}>
              Comment puis-je vous aider ?
              <svg
                width="16"
                height="16"
                viewBox="0 0 24 24"
                fill="none"
                stroke="#0ea5e9"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
                className="inline-block ml-1"
                style={{ verticalAlign: "middle" }}
              >
                <path d="M9 10l3 3-3 3" />
                <path d="M12 13h8" />
              </svg>
            </div>
          )}

          <button
            aria-label="Ouvrir l'assistant"
            onClick={() => setOpen((s) => !s)}
            className="w-14 h-14 bg-primary hover:bg-primary/90 rounded-full shadow-xl flex items-center justify-center text-white"
          >
            {/* Robot SVG animé à yeux clignotants */}
            <svg
              viewBox="0 0 48 48"
              width={36}
              height={36}
              fill="none"
              style={{ display: "block" }}
              className="eye-animate"
            >
              {/* Corps */}
              <rect x="8" y="16" width="32" height="22" rx="11" fill="white" stroke="#0ea5e9" strokeWidth="2" />
              {/* Antenne */}
              <rect x="22" y="8" width="4" height="8" rx="2" fill="#0ea5e9" />
              {/* Oreilles */}
              <circle cx="8" cy="23" r="3" fill="#0ea5e9" />
              <circle cx="40" cy="23" r="3" fill="#0ea5e9" />
              {/* Yeux animés */}
              <ellipse cx="18" cy="27" rx="3" ry="3" fill="#0ea5e9" />
              <ellipse cx="30" cy="27" rx="3" ry="3" fill="#0ea5e9" />
              {/* Bouche */}
              <rect x="20" y="33.5" width="8" height="2" rx="1" fill="#0ea5e9" opacity="0.8" />
            </svg>
          </button>
        </div>
      </div>
    </>
  );
}

client/src/components/cookies/Cookies.ts.content:
// src/components/cookies/Cookies.tsx
import React, { useEffect, useState } from "react";

interface ConsentPreferences {
  analytics: boolean;
  functional: boolean;
  marketing: boolean;
  personalization: boolean;
}

interface ConsentPayload {
  timestamp: string;
  preferences: ConsentPreferences;
}

const CONSENT_KEY = "Covoit'Sport Consent";

export default function Cookies(): JSX.Element | null {
  const [consent, setConsent] = useState<ConsentPayload | null>(null);
  const [showPrefs, setShowPrefs] = useState(false);
  const [analytics, setAnalytics] = useState(true);
  const [functional, setFunctional] = useState(true);
  const [marketing, setMarketing] = useState(false);
  const [personalization, setPersonalization] = useState(true);

  useEffect(() => {
    try {
      const stored = localStorage.getItem(CONSENT_KEY);
      if (stored) setConsent(JSON.parse(stored));
    } catch (e) {
      console.error("Erreur lecture consentement cookies", e);
    }
  }, []);

  function saveConsent(prefs: ConsentPreferences) {
    const payload: ConsentPayload = {
      timestamp: new Date().toISOString(),
      preferences: prefs,
    };
    localStorage.setItem(CONSENT_KEY, JSON.stringify(payload));
    setConsent(payload);
    setShowPrefs(false);
  }

  if (consent) return null;

  return (
    <div
      className="fixed inset-x-2 md:inset-x-6 bottom-6 flex justify-center z-50 transition-all duration-500"
      role="dialog"
      aria-live="polite"
      aria-label="Bannière de cookies"
    >
      <div className="relative max-w-lg md:max-w-4xl w-full bg-gradient-to-br from-white/95 to-sky-50 shadow-xl border border-sky-100 rounded-2xl p-4 md:p-6 flex flex-col md:flex-row gap-3 md:gap-5 items-center">
        {/* Icône cookie */}
        <div
          className="flex items-center justify-center w-14 h-14 rounded-full bg-sky-100 shadow-inner shrink-0 animate-pulse"
          aria-hidden="true"
        >
          <svg width="32" height="32" fill="none" viewBox="0 0 24 24" aria-hidden="true" focusable="false">
            <circle cx="12" cy="12" r="10" fill="#408CFF" />
            <circle cx="9" cy="14" r="1.2" fill="#fff" />
            <circle cx="15" cy="16" r="0.8" fill="#fff" />
            <circle cx="13" cy="10" r="1" fill="#fff" />
            <circle cx="17" cy="9" r="0.7" fill="#fff" />
          </svg>
        </div>
        {/* Message */}
        <div className="flex-1 min-w-0">
          <p className="font-semibold text-gray-900 mb-1">🍪 Nous valorisons votre vie privée</p>
          <p className="text-gray-700 text-sm leading-tight">
            Ce site utilise des cookies pour améliorer l’expérience, personnaliser le contenu, et mesurer l’audience.
            <a href="/privacy" className="underline ml-1 text-primary hover:text-blue-700" tabIndex={0}>
              En savoir plus
            </a>
          </p>
        </div>
        {/* Actions */}
        <div className="flex flex-col gap-2 md:ml-4 md:gap-2 md:flex-row md:items-center">
          <button
            onClick={() => saveConsent({ analytics: false, functional: true, marketing: false, personalization: false })}
            className="px-4 py-2 rounded-lg border border-sky-200 bg-white text-sky-900 font-medium shadow hover:bg-sky-50 focus:outline focus:ring-2 focus:ring-blue-300 transition"
            aria-label="Refuser les cookies analytiques"
          >
            Refuser
          </button>
          <button
            onClick={() => saveConsent({ analytics: true, functional: true, marketing: true, personalization: true })}
            className="px-4 py-2 rounded-lg bg-gradient-to-tr from-sky-500 to-cyan-400 text-white font-semibold shadow hover:from-cyan-600 transition"
            autoFocus
            aria-label="Accepter tous les cookies"
          >
            Accepter
          </button>
          <button
            onClick={() => setShowPrefs(true)}
            className="text-xs md:text-sm underline text-gray-500 hover:text-blue-700 px-3 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 transition-all focus:outline-none focus:ring-2 focus:ring-blue-300"
            aria-label="Gérer les préférences des cookies"
          >
            ⚙️ Préférences cookies
          </button>
        </div>

        {/* Préférences avancées - Version Desktop améliorée */}
        {showPrefs && (
          <div
            className="absolute top-0 left-0 w-full h-full bg-white/95 backdrop-blur-sm rounded-2xl flex flex-col justify-start items-center p-4 md:p-6 z-50 shadow-2xl animate-fade-in max-h-[80vh] overflow-y-auto"
            role="dialog"
            aria-modal="true"
            aria-label="Préférences cookies"
          >
            <div className="text-center mb-4">
              <h3 className="text-lg font-semibold text-gray-900 mb-2">🍪 Préférences des Cookies</h3>
              <p className="text-sm text-gray-600 max-w-md">Personnalisez votre expérience en choisissant les types de cookies que vous acceptez</p>
            </div>
            <form className="flex flex-col gap-4 max-w-md w-full">
              {/* Switch Analytics */}
              <div className="flex items-center justify-between">
                <div className="flex flex-col">
                  <span className="text-sm font-medium">Cookies analytiques</span>
                  <span className="text-xs text-gray-500">Mesure d'audience et performance</span>
                </div>
                <label
                  htmlFor="analytics-toggle"
                  className="inline-flex relative items-center cursor-pointer"
                >
                  <input
                    type="checkbox"
                    id="analytics-toggle"
                    className="sr-only peer"
                    checked={analytics}
                    onChange={() => setAnalytics((a) => !a)}
                  />
                  <div className="w-11 h-6 bg-gray-200 rounded-full peer peer-checked:bg-sky-500 peer-focus:ring-4 peer-focus:ring-sky-300 transition"></div>
                  <div
                    className="absolute left-1 top-1 bg-white w-4 h-4 rounded-full transition-transform peer-checked:translate-x-5"
