                </div>
                <label
                  htmlFor="marketing-toggle"
                  className="inline-flex relative items-center cursor-pointer"
                >
                  <input
                    type="checkbox"
                    id="marketing-toggle"
                    className="sr-only peer"
                    checked={marketing}
                    onChange={() => setMarketing((m) => !m)}
                  />
                  <div className="w-11 h-6 bg-gray-200 rounded-full peer peer-checked:bg-sky-500 peer-focus:ring-4 peer-focus:ring-sky-300 transition"></div>
                  <div
                    className="absolute left-1 top-1 bg-white w-4 h-4 rounded-full transition-transform peer-checked:translate-x-5"
                  ></div>
                </label>
              </div>

              {/* Switch Personalization */}
              <div className="flex items-center justify-between">
                <div className="flex flex-col">
                  <span className="text-sm font-medium">Cookies de personnalisation</span>
                  <span className="text-xs text-gray-500">Contenu adapté à vos préférences</span>
                </div>
                <label
                  htmlFor="personalization-toggle"
                  className="inline-flex relative items-center cursor-pointer"
                >
                  <input
                    type="checkbox"
                    id="personalization-toggle"
                    className="sr-only peer"
                    checked={personalization}
                    onChange={() => setPersonalization((p) => !p)}
                  />
                  <div className="w-11 h-6 bg-gray-200 rounded-full peer peer-checked:bg-sky-500 peer-focus:ring-4 peer-focus:ring-sky-300 transition"></div>
                  <div
                    className="absolute left-1 top-1 bg-white w-4 h-4 rounded-full transition-transform peer-checked:translate-x-5"
                  ></div>
                </label>
              </div>
              <div className="flex gap-2 mt-4">
                <button
                  type="submit"
                  onClick={(e) => {
                    e.preventDefault();
                    saveConsent({ analytics, functional, marketing, personalization });
                  }}
                  className="flex-1 px-4 py-2 rounded-lg bg-sky-500 text-white shadow font-semibold hover:bg-sky-600 transition"
                >
                  Sauvegarder
                </button>
                <button
                  type="button"
                  onClick={() => setShowPrefs(false)}
                  className="flex-1 px-4 py-2 rounded-lg border border-gray-300 text-gray-600 bg-white hover:bg-gray-100 transition"
                >
                  Annuler
                </button>
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/dashboard/Events.t.content:
// client/src/components/dashboard/Events.tsx
import { useState } from "react";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import EventModal from "@/components/modals/EventModal";
import InviteModal from "@/components/modals/InviteModal";
import ParticipantManagementModal from "@/components/modals/ParticipantManagementModal";
import type { Event, EventParticipant } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { jsPDF } from "jspdf";
import { saveAs } from "file-saver";
import EventDetailsModal from "../modals/EventDetails";

export default function Events() {
  const [selectedEvent, setSelectedEvent] = useState<Event | null>(null);
  const [selectedEventId, setSelectedEventId] = useState<string | null>(null);
  const [showEventModal, setShowEventModal] = useState(false);
  const [showInviteModal, setShowInviteModal] = useState(false);
  const [showDetailsModal, setShowDetailsModal] = useState(false);
  const [showParticipantManagement, setShowParticipantManagement] = useState(false);

  // Filter states
  const [statusFilter, setStatusFilter] = useState<string>("all");
  const [sportFilter, setSportFilter] = useState<string>("all");

  const queryClient = useQueryClient();
  const { toast } = useToast();

  const { data: allEvents = [], isLoading } = useQuery<Event[]>({
    queryKey: ["/api/events"],
  });

  // Filter events based on selected filters
  const events = allEvents.filter((event) => {
    // Status filter
    if (statusFilter !== "all") {
      const now = new Date();
      const eventDate = new Date(event.date);

      switch (statusFilter) {
        case "upcoming":
          if (eventDate <= now) return false;
          break;
        case "past":
          if (eventDate > now) return false;
          break;
        case "recurring":
          if (!event.isRecurring) return false;
          break;
        default:
          break;
      }
    }

    // Sport filter
    if (sportFilter !== "all" && event.sportType !== sportFilter) {
      return false;
    }

    return true;
  });

  const handleInvite = (eventId: string) => {
    setSelectedEventId(eventId);
    setShowInviteModal(true);
  };

  const handleEdit = (event: Event) => {
    setSelectedEvent(event);
    setShowEventModal(true);
  };

  const handleDetails = (event: Event) => {
    setSelectedEvent(event);
    setShowDetailsModal(true);
  };

  const handleDelete = async (eventId: string) => {
    if (!confirm("Supprimer cet événement ?")) return;
    try {
      await apiRequest("DELETE", `/api/events/${eventId}`);
      toast({ title: "Événement supprimé" });
      queryClient.invalidateQueries({ queryKey: ["/api/events"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] }); // 🔹 important
    } catch (err: any) {
      toast({
        title: "Erreur",
        description: err?.message || "Suppression impossible",
        variant: "destructive",
      });
    }
  };

  const handleParticipantManagement = (eventId: string) => {
    setSelectedEventId(eventId);
    setShowParticipantManagement(true);
  };

  const handleGenerateLink = async (eventId: string) => {
    try {
      const res = await apiRequest("POST", `/api/events/${eventId}/invitations`);
      // si apiRequest renvoie directement Response, lire json :
      const data = await res.json();
      const { token } = data;
      const link = `${window.location.origin}/events/${eventId}`;
      await navigator.clipboard.writeText(link);
      toast({
        title: "Lien copié",
        description: "Le lien d'invitation a été copié dans le presse-papier."
      });
    } catch (err: any) {
      toast({
        title: "Erreur",
        description: err?.message || "Impossible de générer le lien",
        variant: "destructive"
      });
    }
  };

  // --- EXPORT CSV ---
  const exportCSV = () => {
    const rows = [["Nom", "Date", "Lieu", "Destination", "Participants"]];
    events.forEach((e: any) => {
      rows.push([
        e.name,
        new Date(e.date).toLocaleString("fr-FR"),
        e.meetingPoint,
        e.destination,
        e.participantsCount?.toString() || "0"
      ]);
    });
    const csvContent = rows
      .map(row => row.map(cell => `"${String(cell).replace(/"/g, '""')}"`).join(","))
      .join("\n");
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
    saveAs(blob, "events.csv");
  };

  // --- EXPORT PDF ---
  const exportPDF = () => {
    const doc = new jsPDF();
    let y = 10;
    doc.setFontSize(14);
    doc.text("Liste des événements", 10, y);
    y += 10;
    doc.setFontSize(11);

    events.forEach((e: any, i: number) => {
      doc.text(`${i + 1}. ${e.name} — ${new Date(e.date).toLocaleString("fr-FR")}`, 10, y);
      y += 6;
      doc.text(`Lieu: ${e.meetingPoint} → ${e.destination}`, 10, y);
      y += 10;
      if (y > 270) {
        doc.addPage();
        y = 10;
      }
    });

    doc.save("events.pdf");
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="animate-fade-in">
      <div className="flex items-center justify-between mb-8">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Mes événements</h1>
          <p className="text-gray-600">Créez et gérez tous vos événements sportifs.</p>
        </div>
        <Button onClick={() => { setSelectedEvent(null); setShowEventModal(true); }} className="bg-primary hover:bg-blue-700">
          <i className="fas fa-plus mr-2"></i>
          Nouvel événement
        </Button>
      </div>

      {/* Events Filter */}
      <Card className="mb-8">
        <CardContent className="p-6">
          <div className="flex flex-wrap gap-4">
            <div className="flex items-center space-x-2">
              <label className="text-sm font-medium text-gray-700">Filtrer :</label>
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-48">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Tous les événements</SelectItem>
                  <SelectItem value="upcoming">À venir</SelectItem>
                  <SelectItem value="past">Passés</SelectItem>
                  <SelectItem value="recurring">Récurrents</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div className="flex items-center space-x-2">
              <label className="text-sm font-medium text-gray-700">Sport :</label>
              <Select value={sportFilter} onValueChange={setSportFilter}>
                <SelectTrigger className="w-48">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Tous les sports</SelectItem>
                  <SelectItem value="football">Football</SelectItem>
                  <SelectItem value="basketball">Basketball</SelectItem>
                  <SelectItem value="tennis">Tennis</SelectItem>
                  <SelectItem value="running">Course à pied</SelectItem>
                  <SelectItem value="volleyball">Volleyball</SelectItem>
                  <SelectItem value="natation">Natation</SelectItem>
                  <SelectItem value="cyclisme">Cyclisme</SelectItem>
                  <SelectItem value="autre">Autre</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div className="flex items-center space-x-2">
              <Button 
                variant="outline" 
                size="sm"
                onClick={() => {
                  setStatusFilter("all");
                  setSportFilter("all");
                }}
                className="text-xs"
              >
                <i className="fas fa-times mr-1"></i>
                Réinitialiser
              </Button>
            </div>
            <div className="ml-auto flex space-x-2">
              <Button variant="outline" onClick={exportCSV}>
                <i className="fas fa-file-csv mr-2"></i> CSV
              </Button>
              <Button variant="outline" onClick={exportPDF}>
                <i className="fas fa-file-pdf mr-2 text-red-500"></i> PDF
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Events List */}
      {events.length === 0 ? (
        <Card>
          <CardContent className="p-12">
            <div className="text-center">
              <i className="fas fa-calendar-plus text-gray-400 text-6xl mb-6"></i>
              <h3 className="text-xl font-semibold text-gray-900 mb-2">Aucun événement</h3>
              <p className="text-gray-600 mb-6">
                Vous n'avez pas encore créé d'événement. Commencez par créer votre premier événement !
              </p>
              <Button onClick={() => { setSelectedEvent(null); setShowEventModal(true); }} className="bg-primary hover:bg-blue-700">
                <i className="fas fa-plus mr-2"></i>
                Créer mon premier événement
              </Button>
            </div>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-6">
          {events.map((event) => (
            <EventCard
              key={event.id}
              event={event}
              onInvite={() => handleInvite(event.id)}
              onEdit={() => handleEdit(event)}
              onDelete={() => handleDelete(event.id)}
              onGenerateLink={() => handleGenerateLink(event.id)}
              onDetails={() => handleDetails(event)}
              onManageParticipants={() => handleParticipantManagement(event.id)}
            />
          ))}
        </div>
      )}

      {/* Modals */}
      <EventModal
        isOpen={showEventModal}
        onClose={() => setShowEventModal(false)}
        event={selectedEvent || undefined}
      />

      <InviteModal
        isOpen={showInviteModal}
        onClose={() => setShowInviteModal(false)}
        eventId={selectedEventId}
      />
      <EventDetailsModal
        isOpen={showDetailsModal}
        onClose={() => setShowDetailsModal(false)}
        event={selectedEvent || undefined}
      />

      <ParticipantManagementModal
        isOpen={showParticipantManagement}
        onClose={() => setShowParticipantManagement(false)}
        eventId={selectedEventId}
      />

      {/* Vous pouvez afficher un modal détails si nécessaire en utilisant selectedEvent et showDetailsModal */}
    </div>
  );
}

/* EventCard now receives callbacks as props instead of accessing parent's state directly */
function EventCard({
  event,
  onInvite,
  onEdit,
  onDelete,
  onGenerateLink,
  onDetails,
  onManageParticipants,
}: {
  event: Event;
  onInvite: () => void;
  onEdit: () => void;
  onDelete: () => void;
  onGenerateLink: () => void;
  onDetails: () => void;
  onManageParticipants: () => void;
}) {
  const { data: participants = [], error: participantsError } = useQuery<EventParticipant[]>({
    queryKey: [`/api/events/${event.id}/participants`],
    retry: 2,
    staleTime: 1000 * 60 * 2, // 2 minutes
  });

  // Sécurisation de l'accès aux participants avec debug
  let participantsArray: EventParticipant[] = [];

  if (participants === null || participants === undefined) {
    console.warn(`⚠️ Participants pour l'événement ${event.id}: données nulles/undefined`);
    participantsArray = [];
  } else if (Array.isArray(participants)) {
    participantsArray = participants;
  } else {
    console.error(`❌ participants N'EST PAS un tableau !`, typeof participants, participants);
    participantsArray = [];
  }

  if (participantsError) {
    console.error(`❌ Erreur lors du chargement des participants pour l'événement ${event.id}:`, participantsError);
  }

  const drivers = participantsArray.filter(p => p && p.role === "driver");
  const passengers = participantsArray.filter(p => p && p.role === "passenger");
  const totalSeats = drivers.reduce((sum, d) => sum + (d?.availableSeats || 0), 0);
  const availableSeats = Math.max(0, totalSeats - passengers.length);

  return (
    <Card className="overflow-hidden">
      <CardContent className="p-6">
        <div className="flex items-start justify-between">
          <div className="flex items-center space-x-4">
            <div className="w-16 h-16 bg-primary/10 rounded-xl flex items-center justify-center">
              <i className="fas fa-running text-primary text-2xl"></i>
            </div>
            <div>
              <h3 className="text-xl font-semibold text-gray-900">{event.name}</h3>
              <p className="text-gray-600 mt-1">
                <i className="fas fa-calendar mr-2"></i>
                {new Date(event.date).toLocaleDateString('fr-FR', {
                  weekday: 'long',
                  year: 'numeric',
                  month: 'long',
                  day: 'numeric'
                })}
                <span className="mx-2">•</span>
                <i className="fas fa-clock mr-2"></i>
                {new Date(event.date).toLocaleTimeString('fr-FR', {
                  hour: '2-digit',
                  minute: '2-digit'
                })}
              </p>
              <div className="flex items-center mt-2 text-sm text-gray-600">
                <i className="fas fa-map-marker-alt mr-2"></i>
                <span>{event.meetingPoint}</span>
                <i className="fas fa-arrow-right mx-3"></i>
                <span>{event.destination}</span>
              </div>
            </div>
          </div>
          <div className="flex items-center space-x-2">
            <span className="px-3 py-1 bg-green-100 text-green-800 rounded-full text-sm font-medium">
              {event.status === "confirmed" ? "Confirmé" : event.status}
            </span>
            <div className="flex items-center space-x-2">
              <Button variant="ghost" size="sm" onClick={onEdit}>
                <i className="fas fa-edit"></i>
              </Button>
              <Button variant="ghost" size="sm" className="text-red-500 hover:text-red-700" onClick={onDelete}>
                <i className="fas fa-trash"></i>
              </Button>
            </div>
          </div>
        </div>

        <div className="grid md:grid-cols-3 gap-6 mt-6">
          <div className="space-y-2">
            <h4 className="font-medium text-gray-900">Participants</h4>
            <div className="text-2xl font-bold text-primary">{participants.length}</div>
            <div className="text-sm text-gray-600">
              {drivers.length} conducteurs • {passengers.length} passagers
            </div>
          </div>

          <div className="space-y-2">
            <h4 className="font-medium text-gray-900">Places disponibles</h4>
            <div className="text-2xl font-bold text-secondary">{availableSeats}</div>
            <div className="text-sm text-gray-600">dans {drivers.length} véhicules</div>
          </div>

          <div className="space-y-2">
            <h4 className="font-medium text-gray-900">Actions</h4>
            <div className="flex flex-wrap gap-2">
              <Button onClick={onInvite} size="sm" className="bg-primary hover:bg-blue-700">
                <i className="fas fa-user-plus mr-1"></i>
                Inviter
              </Button>

              <Button
                variant="outline"
                size="sm"
                onClick={onGenerateLink}
              >
                <i className="fas fa-link mr-1"></i>
                Générer le lien
              </Button>

              <Button
                variant="outline"
                size="sm"
                onClick={onDetails}
              >
                <i className="fas fa-eye mr-1"></i>
                Détails
              </Button>

              <Button
                variant="outline"
                size="sm"
                onClick={onManageParticipants}
                className="bg-blue-50 hover:bg-blue-100"
              >
                <i className="fas fa-users-cog mr-1"></i>
                Gérer
              </Button>
            </div>
          </div>
        </div>

        {/* Participants Preview */}
        {participants.length > 0 && (
          <div className="mt-6 pt-6 border-t border-gray-200">
            <h4 className="font-medium text-gray-900 mb-3">Participants récents</h4>
            <div className="flex items-center space-x-4 text-sm">
              {participants.slice(0, 3).map((participant: any, index: number) => (
                <div key={index} className="flex items-center space-x-2">
                  <div className="w-8 h-8 bg-gray-200 rounded-full flex items-center justify-center">
                    <i className="fas fa-user text-gray-500"></i>
                  </div>
                  <span className="text-gray-700">{participant.name}</span>
                  <span className={`px-2 py-1 rounded-full text-xs ${participant.role === "driver"
                    ? "bg-primary text-white"
                    : "bg-gray-200 text-gray-700"
                    }`}>
                    {participant.role === "driver" ? "Conducteur" : "Passager"}
                  </span>
                </div>
              ))}
              {participants.length > 3 && (
                <Button variant="ghost" size="sm" className="text-primary hover:text-blue-700">
                  +{participants.length - 3} autres
                </Button>
              )}
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/dashboard/FeatureS.content:
import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { useFeatures, FEATURE_DESCRIPTIONS } from "@/hooks/useFeatures";
import { useAuth } from "@/hooks/useAuth";

export function FeatureStatus() {
  const { getActiveFeatures, getFeatureInfo, isOrganizationDisabled, organization } = useFeatures();
  const { isAuthenticated } = useAuth();

  if (!isAuthenticated || !organization) return null;

  const activeFeatures = getActiveFeatures();
  const isDisabled = isOrganizationDisabled();

  // Grouper les fonctionnalités par catégorie
  const featuresByCategory = activeFeatures.reduce((acc, feature) => {
    const info = getFeatureInfo(feature);
    if (!info) return acc;

    const category = info.category || 'Autres';
    if (!acc[category]) {
      acc[category] = [];
    }
    acc[category].push({ id: feature, ...info });
    return acc;
  }, {} as Record<string, Array<{ id: string; name: string; description: string; icon: string; category: string }>>);

  if (isDisabled) {
    return (
      <Card className="border-red-200 bg-red-50 dark:border-red-800 dark:bg-red-950/30">
        <CardContent className="p-6">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 bg-red-100 dark:bg-red-900/50 rounded-lg flex items-center justify-center">
              <i className="fas fa-exclamation-triangle text-red-600 dark:text-red-400"></i>
            </div>
            <div>
              <h3 className="text-lg font-semibold text-red-900 dark:text-red-100">
                Organisation désactivée
              </h3>
              <p className="text-sm text-red-700 dark:text-red-300">
                Votre organisation a été temporairement désactivée. Contactez l'administrateur pour plus d'informations.
              </p>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (activeFeatures.length === 0 && organization.role !== 'admin') {
    return (
      <Card className="border-yellow-200 bg-yellow-50 dark:border-yellow-800 dark:bg-yellow-950/30">
        <CardContent className="p-6">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 bg-yellow-100 dark:bg-yellow-900/50 rounded-lg flex items-center justify-center">
              <i className="fas fa-info-circle text-yellow-600 dark:text-yellow-400"></i>
            </div>
            <div>
              <h3 className="text-lg font-semibold text-yellow-900 dark:text-yellow-100">
                Aucune fonctionnalité activée
              </h3>
              <p className="text-sm text-yellow-700 dark:text-yellow-300">
                Contactez votre administrateur pour activer des fonctionnalités supplémentaires.
              </p>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <span className="flex items-center">
            <i className="fas fa-cog mr-2 text-primary"></i>
            Fonctionnalités activées
          </span>
          <Badge variant="outline">
            {organization.role === 'admin' ? 'Administrateur' : `${activeFeatures.length} fonctionnalités`}
          </Badge>
        </CardTitle>
      </CardHeader>
      <CardContent className="p-6">
        {organization.role === 'admin' ? (
          <div className="text-center py-4">
            <div className="w-16 h-16 bg-blue-100 dark:bg-blue-900/30 rounded-full flex items-center justify-center mx-auto mb-3">
              <i className="fas fa-crown text-2xl text-blue-600 dark:text-blue-400"></i>
            </div>
            <h3 className="text-lg font-semibold mb-2">Accès administrateur</h3>
            <p className="text-sm text-muted-foreground">
              Vous avez accès à toutes les fonctionnalités de la plateforme.
            </p>
          </div>
        ) : (
          <div className="space-y-6">
            {Object.entries(featuresByCategory).map(([category, features]) => (
              <div key={category}>
                <h4 className="text-sm font-medium text-muted-foreground mb-3 uppercase tracking-wider">
                  {category}
                </h4>
                <div className="grid gap-3">
                  {features.map((feature) => (
                    <div key={feature.id} className="flex items-center space-x-3 p-3 rounded-lg bg-muted/30">
                      <div className="w-8 h-8 bg-primary/10 rounded-lg flex items-center justify-center">
                        <i className={`${feature.icon} text-primary text-sm`}></i>
                      </div>
                      <div className="flex-1">
                        <h5 className="font-medium text-sm">{feature.name}</h5>
                        <p className="text-xs text-muted-foreground">{feature.description}</p>
                      </div>
                      <Badge variant="default" className="text-xs">
                        <i className="fas fa-check mr-1"></i>
                        Actif
                      </Badge>
                    </div>
                  ))}
                </div>
              </div>
            ))}

            {activeFeatures.length > 0 && (
              <div className="pt-4 border-t">
                <p className="text-xs text-muted-foreground text-center">
                  Ces fonctionnalités sont activées pour votre organisation.
                  <br />
                  Contactez l'administrateur pour modifier vos permissions.
                </p>
              </div>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

// Composant simple pour afficher l'indicateur de statut dans la barre de navigation
export function OrganizationStatusIndicator() {
  const { organization, isOrganizationDisabled } = useFeatures();
  const { isAuthenticated } = useAuth();

  if (!isAuthenticated || !organization) return null;

  if (isOrganizationDisabled()) {
    return (
      <Badge variant="destructive" className="text-xs">
        <i className="fas fa-exclamation-triangle mr-1"></i>
        Désactivée
      </Badge>
    );
  }

  if (organization.role === 'admin') {
    return (
      <Badge variant="default" className="text-xs bg-blue-600">
        <i className="fas fa-crown mr-1"></i>
        Admin
      </Badge>
    );
  }

  return (
    <Badge variant="secondary" className="text-xs">
      <i className="fas fa-check-circle mr-1"></i>
      Actif
    </Badge>
  );
}

client/src/components/dashboard/Messages.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import type { Event, Message } from "@shared/schema";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import BroadcastMessageModal from "@/components/messaging/BroadcastMessageModal";
import { useMessaging } from "@/contexts/MessagingContext";

interface Conversation {
  eventId: string;
  eventName: string;
  lastMessage: string;
  lastMessageTime: string;
  unreadCount: number;
}

export default function Messages() {
  const [selectedConversation, setSelectedConversation] = useState<string | null>(null);
  const [messageContent, setMessageContent] = useState("");
  const [showBroadcastModal, setShowBroadcastModal] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState<Event | null>(null);

  const { data: events = [] } = useQuery<Event[]>({
    queryKey: ["/api/events"],
  });



  const queryClient = useQueryClient();

  const sendMessageMutation = useMutation({
    mutationFn: async () => {
      const { apiRequest } = await import("@/lib/queryClient");
      return await apiRequest("POST", `/api/events/${selectedConversation}/messages`, { 
        content: messageContent 
      });
    },
    onSuccess: () => {
      setMessageContent("");
      queryClient.invalidateQueries({ queryKey: [`/api/events/${selectedConversation}/messages`] });
    },
    onError: (error: any) => {
      console.error("Send message error:", error);
    }
  });

  const deleteMessageMutation = useMutation({
    mutationFn: async (messageId: string) => {
      const { apiRequest } = await import("@/lib/queryClient");
      return await apiRequest("DELETE", `/api/messages/${messageId}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/events/${selectedConversation}/messages`] });
    },
    onError: (error: any) => {
      console.error("Delete message error:", error);
    }
  });

  const handleDeleteMessage = (id: string) => {
    if (confirm("Supprimer ce message ?")) {
      deleteMessageMutation.mutate(id);
    }
  };



  // Group messages by event and get conversations
  const conversations: Conversation[] = events.map((event) => ({
    eventId: event.id,
    eventName: event.name,
    lastMessage: "Dernière activité...",
    lastMessageTime: "14:32",
    unreadCount: Math.floor(Math.random() * 3),
  }));

  const { data: messages = [] } = useQuery<Message[]>({
    queryKey: [`/api/events/${selectedConversation}/messages`],
    enabled: !!selectedConversation,
  });

  const handleSendMessage = () => {
    if (!messageContent.trim() || !selectedConversation) return;
    sendMessageMutation.mutate();
  };

  const handleBroadcastMessage = (event: Event) => {
    setSelectedEvent(event);
    setShowBroadcastModal(true);
  };

  return (
    <div className="animate-fade-in">
      <div className="mb-8">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 mb-2">Messages</h1>
            <p className="text-gray-600">Communiquez avec les participants de vos événements.</p>
          </div>
          <Button
            onClick={() => {
              if (events.length > 0) {
                setSelectedEvent(events[0]);
                setShowBroadcastModal(true);
              }
            }}
            className="bg-primary hover:bg-blue-700"
            disabled={events.length === 0}
          >
            <i className="fas fa-bullhorn mr-2"></i>
            Diffuser un message
          </Button>
        </div>
      </div>

      <Card className="h-96 flex">
        {/* Messages List */}
        <div className="w-1/3 border-r border-gray-200 overflow-y-auto">
          <div className="p-4 border-b border-gray-200">
            <div className="relative">
              <i className="fas fa-search absolute left-3 top-3 text-gray-400"></i>
              <Input
                placeholder="Rechercher..."
                className="pl-10"
              />
            </div>
          </div>

          {conversations.length === 0 ? (
            <div className="p-8 text-center">
              <i className="fas fa-comments text-gray-400 text-4xl mb-4"></i>
              <p className="text-gray-600">Aucune conversation</p>
              <p className="text-gray-500 text-sm">Les messages des participants apparaîtront ici</p>
            </div>
          ) : (
            conversations.map((conversation) => (
              <div
                key={conversation.eventId}
                className={`p-4 border-b border-gray-200 hover:bg-gray-50 cursor-pointer transition-colors ${selectedConversation === conversation.eventId ? "bg-blue-50" : ""
                  }`}
                onClick={() => setSelectedConversation(conversation.eventId)}
              >
                <div className="flex items-center space-x-3">
                  <Avatar className="w-10 h-10">
                    <AvatarFallback>
                      <i className="fas fa-calendar text-gray-500"></i>
                    </AvatarFallback>
                  </Avatar>
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center justify-between">
                      <p className="font-medium text-gray-900 truncate">{conversation.eventName}</p>
                      <span className="text-xs text-gray-500">{conversation.lastMessageTime}</span>
                    </div>
                    <p className="text-sm text-gray-600 truncate">{conversation.lastMessage}</p>
                    <div className="flex items-center justify-between mt-1">
                      <span className="text-xs text-gray-500">Événement</span>
                      {conversation.unreadCount > 0 && (
                        <span className="w-2 h-2 bg-red-500 rounded-full"></span>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>

        {/* Chat Area */}
        <div className="flex-1 flex flex-col">
          {selectedConversation ? (
            <>
              <div className="p-4 border-b border-gray-200 bg-gray-50">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <Avatar className="w-10 h-10">
                      <AvatarFallback>
                        <i className="fas fa-calendar text-gray-500"></i>
                      </AvatarFallback>
                    </Avatar>
                    <div>
                      <p className="font-medium text-gray-900">
                        {conversations.find(c => c.eventId === selectedConversation)?.eventName}
                      </p>
                      <p className="text-sm text-gray-600">Conversation de l'événement</p>
                    </div>
                  </div>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      const event = events.find(e => e.id === selectedConversation);
                      if (event) handleBroadcastMessage(event);
                    }}
                  >
                    <i className="fas fa-bullhorn mr-1"></i>
                    Diffuser
                  </Button>
                </div>
              </div>

              {/* Messages */}
              <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {messages.length === 0 ? (
                  <div className="text-center py-8">
                    <i className="fas fa-comment-dots text-gray-400 text-3xl mb-2"></i>
                    <p className="text-gray-600">Aucun message pour cet événement</p>
                  </div>
                ) : ([...messages].reverse().map((message: any) => (
                      <div
                        key={message.id}
                        className={`flex ${message.isFromOrganizer ? "justify-end" : "justify-start"}`}
                      >
                        {!message.isFromOrganizer && (
                          <Avatar className="w-8 h-8 mr-2">
                            <AvatarFallback>
                              {message.senderName.slice(0, 2).toUpperCase()}
                            </AvatarFallback>
                          </Avatar>
                        )}

                        <div
                          className={`relative rounded-2xl p-3 max-w-xs sm:max-w-sm md:max-w-md ${message.isFromOrganizer
                            ? "bg-blue-500 text-white rounded-br-none"
                            : "bg-gray-200 text-gray-900 rounded-bl-none"
                            }`}
                        >
                          <p className="text-sm">{message.content}</p>
                          <p className="text-[10px] opacity-70 mt-1">
                            {message.senderName} •{" "}
                            {new Date(message.createdAt).toLocaleTimeString("fr-FR", {
                              hour: "2-digit",
                              minute: "2-digit",
                            })}
                          </p>

                          {message.isFromOrganizer && (
                            <button
                              onClick={() => handleDeleteMessage(message.id)}
                              className="text-red-600 hover:text-red-800"
                              title="Supprimer ce message"
                            >
                              <i className="fas fa-trash text-xs"></i>
                            </button>
                          )}
                        </div>
                      </div>
                    ))

                )}
              </div>

              {/* Message Input */}
              <div className="p-4 border-t border-gray-200">
                <div className="flex items-center space-x-3">
                  <Input
                    value={messageContent}
                    onChange={(e) => setMessageContent(e.target.value)}
                    placeholder="Tapez votre message..."
                    onKeyPress={(e) => e.key === "Enter" && handleSendMessage()}
                  />
                  <Button
                    onClick={handleSendMessage}
                    disabled={!messageContent.trim()}
                    className="bg-primary hover:bg-blue-700"
                  >
                    <i className="fas fa-paper-plane"></i>
                  </Button>
                </div>
              </div>
            </>
          ) : (
            <div className="flex-1 flex items-center justify-center">
              <div className="text-center">
                <i className="fas fa-comments text-gray-400 text-4xl mb-4"></i>
                <p className="text-gray-600">Sélectionnez une conversation</p>
                <p className="text-gray-500 text-sm">Choisissez un événement pour voir les messages</p>
              </div>
            </div>
          )}
        </div>
      </Card>

      <BroadcastMessageModal
        isOpen={showBroadcastModal}
        onClose={() => setShowBroadcastModal(false)}
        event={selectedEvent}
      />
    </div>
  );
}

client/src/components/dashboard/Overview.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useAuth } from "@/hooks/useAuth";
import { useStats } from "@/contexts/StatsContext";
import { useFeatures, AVAILABLE_FEATURES, FeatureGuard } from "@/hooks/useFeatures";
import { CsvImportModal } from "@/components/import/CsvImportModal";
import { FeatureStatus } from "@/components/dashboard/FeatureStatus";
import SubscriptionInfo from "@/components/subscription/SubscriptionInfo";
import type { Event } from "@shared/schema";
import { useEffect, useState } from "react";

interface DashboardStats {
  // Selon besoins utilisateur
  activeEvents: number; // Total événements créés par l'organisateur  
  totalParticipants: number; // Total participants (passagers + conducteurs)
  totalDrivers: number; // Total conducteurs
  availableSeats: number; // Places disponibles (sièges totaux - occupés)

  // Statistiques détaillées
  totalEventsCreated?: number;
  activeEventsCount?: number; 
  completedEventsCount?: number;
  totalSeats?: number;
  occupiedSeats?: number;
  activeEventStats?: {
    participants: number;
    drivers: number;
    seats: number;
    occupiedSeats: number;
  };
}

export default function Overview() {
  const { organization } = useAuth();
  const { hasFeature } = useFeatures();
  const [showCsvImport, setShowCsvImport] = useState(false);

  // Use the real-time stats context
  const { stats, isLoading: statsLoading, error: statsError, refreshStats } = useStats();

  const { data: events = [], refetch: refetchEvents } = useQuery<Event[]>({
    queryKey: ["/api/events"],
    refetchInterval: 60000, // Refetch every minute
    retry: 2,
    staleTime: 1000 * 60 * 2, // 2 minutes
  });

  // Auto-refresh stats when component mounts - now handled by StatsContext
  useEffect(() => {
    const interval = setInterval(() => {
      refreshStats();
      refetchEvents();
    }, 15000); // Refetch every 15 seconds

    return () => clearInterval(interval);
  }, [refreshStats, refetchEvents]);

  // Debug des erreurs
  useEffect(() => {
    if (statsError) {
      console.error('❌ Erreur lors du chargement des statistiques:', statsError);
    }
  }, [statsError]);

  const recentEvents = events.slice(0, 2);

  return (
    <div className="animate-fade-in">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">
          Bienvenue, {organization?.name}
        </h1>
        <p className="text-gray-600">Gérez vos événements et covoiturages en un coup d'œil.</p>
      </div>

      {/* Stats Cards */}
      <div className="grid md:grid-cols-4 gap-6 mb-8">
        <Card className="hover:shadow-lg transition-shadow duration-200">
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-primary/10 rounded-lg flex items-center justify-center">
                <i className="fas fa-calendar-plus text-primary"></i>
              </div>
              <div className="ml-4">
                <p className="text-muted-foreground text-sm">Événements créés</p>
                <p className="text-2xl font-bold" data-testid="active-events-count">{stats?.activeEvents || 0}</p>
                <p className="text-xs text-muted-foreground mt-1">
                  {stats?.activeEventsCount || 0} actifs • {stats?.completedEventsCount || 0} terminés
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="hover:shadow-lg transition-shadow duration-200">
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-secondary/10 rounded-lg flex items-center justify-center">
                <i className="fas fa-users text-secondary"></i>
              </div>
              <div className="ml-4">
                <p className="text-muted-foreground text-sm">Participants</p>
                <p className="text-2xl font-bold" data-testid="participants-count">{stats?.totalParticipants || 0}</p>
                <p className="text-xs text-muted-foreground mt-1">
                  Passagers + Conducteurs
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="hover:shadow-lg transition-shadow duration-200">
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
                <i className="fas fa-car text-blue-600"></i>
              </div>
              <div className="ml-4">
                <p className="text-muted-foreground text-sm">Conducteurs</p>
                <p className="text-2xl font-bold" data-testid="drivers-count">{stats?.totalDrivers || 0}</p>
                <p className="text-xs text-muted-foreground mt-1">
                  {stats?.activeEventStats?.drivers || 0} dans événements actifs
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="hover:shadow-lg transition-shadow duration-200">
          <CardContent className="p-6">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center">
                <i className="fas fa-chair text-green-600"></i>
              </div>
              <div className="ml-4">
                <p className="text-muted-foreground text-sm">Places disponibles</p>
                <p className="text-2xl font-bold" data-testid="available-seats-count">{stats?.availableSeats || 0}</p>
                <p className="text-xs text-muted-foreground mt-1">
                  Dans les événements actifs
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Statut des fonctionnalités */}
      <div className="mb-8">
        <FeatureStatus />
      </div>

      {/* Informations d'abonnement */}
      <div className="mb-8">
        <SubscriptionInfo />
      </div>

      {/* Analyse détaillée et statistiques avancées */}
      <div className="grid md:grid-cols-2 gap-6 mb-8">
        <Card>
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold mb-4 flex items-center">
              <i className="fas fa-chart-pie text-primary mr-2"></i>
              Analyse des événements
            </h3>
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <span className="text-sm text-muted-foreground">Taux de participation</span>
                <span className="text-sm font-medium">
                  {stats?.totalParticipants && stats?.activeEvents 
                    ? Math.round((stats.totalParticipants / Math.max(1, stats.activeEvents)) * 100) / 100
                    : 0} participants/événement
                </span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-muted-foreground">Taux d'occupation</span>
                <span className="text-sm font-medium">
                  {stats?.activeEventStats?.seats && stats?.activeEventStats?.occupiedSeats
                    ? Math.round((stats.activeEventStats.occupiedSeats / Math.max(1, stats.activeEventStats.seats)) * 100)
                    : 0}%
                </span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-muted-foreground">Conducteurs actifs</span>
                <span className="text-sm font-medium">
                  {stats?.activeEventStats?.drivers || 0} conducteurs
                </span>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold mb-4 flex items-center">
              <i className="fas fa-bolt text-yellow-500 mr-2"></i>
              Actions rapides
            </h3>
            <div className="space-y-2">
              <Button variant="outline" className="w-full justify-start" size="sm">
                <i className="fas fa-plus mr-2"></i>
                Créer un nouvel événement
              </Button>
              <Button variant="outline" className="w-full justify-start" size="sm">
                <i className="fas fa-envelope mr-2"></i>
                Envoyer des rappels
              </Button>
              <FeatureGuard feature={AVAILABLE_FEATURES.CSV_IMPORT}>
                <Button 
                  variant="outline" 
                  className="w-full justify-start" 
                  size="sm"
                  onClick={() => setShowCsvImport(true)}
                >
                  <i className="fas fa-file-csv mr-2"></i>
                  Importer emails CSV/Excel
                </Button>
              </FeatureGuard>
              <Button variant="outline" className="w-full justify-start" size="sm">
                <i className="fas fa-download mr-2"></i>
                Exporter les données
              </Button>
              <FeatureGuard feature={AVAILABLE_FEATURES.ANALYTICS}>
                <Button variant="outline" className="w-full justify-start" size="sm">
                  <i className="fas fa-chart-bar mr-2"></i>
                  Voir les rapports détaillés
                </Button>
              </FeatureGuard>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Recent Events */}
      <Card>
        <CardContent className="p-6">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-xl font-semibold">Événements récents</h2>
            <Button variant="ghost" className="text-primary hover:text-primary/80">
              Voir tout <i className="fas fa-arrow-right ml-1"></i>
            </Button>
          </div>

          {recentEvents.length === 0 ? (
            <div className="text-center py-8">
              <i className="fas fa-calendar-plus text-muted-foreground text-4xl mb-4"></i>
              <p className="text-muted-foreground">Aucun événement créé pour le moment.</p>
              <p className="text-muted-foreground text-sm">Créez votre premier événement pour commencer !</p>
              <Button className="mt-4" size="sm">
                <i className="fas fa-plus mr-2"></i>
                Créer un événement
              </Button>
            </div>
          ) : (
            <div className="space-y-4">
              {recentEvents.map((event: any) => {
                const eventDate = new Date(event.eventDate || event.date);
                const isUpcoming = eventDate >= new Date();
                const participantCount = event.participants?.length || 0;

                return (
                  <div 
                    key={event.id}
                    className="flex items-center justify-between p-4 border rounded-lg hover:bg-muted/50 transition-colors"
                  >
                    <div className="flex items-center space-x-4">
                      <div className={`w-12 h-12 rounded-lg flex items-center justify-center ${
                        isUpcoming ? 'bg-primary/10' : 'bg-muted'
                      }`}>
                        <i className={`fas fa-running ${isUpcoming ? 'text-primary' : 'text-muted-foreground'}`}></i>
                      </div>
                      <div>
                        <h3 className="font-semibold">{event.name}</h3>
                        <p className="text-sm text-muted-foreground">
                          <i className="fas fa-calendar mr-1"></i>
                          {eventDate.toLocaleDateString('fr-FR')}
                          <i className="fas fa-map-marker-alt ml-4 mr-1"></i>
                          {event.destination}
                        </p>
                        <p className="text-xs text-muted-foreground">
                          {participantCount} participant{participantCount !== 1 ? 's' : ''}
                        </p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-4">
                      <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                        isUpcoming 
                          ? 'bg-green-100 text-green-800 dark:bg-green-900/30 dark:text-green-400' 
                          : 'bg-gray-100 text-gray-800 dark:bg-gray-900/30 dark:text-gray-400'
                      }`}>
                        {isUpcoming ? "À venir" : "Terminé"}
                      </span>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Modal d'import CSV */}
      <CsvImportModal
        isOpen={showCsvImport}
        onClose={() => setShowCsvImport(false)}
        onImportComplete={(emails) => {
          console.log('Emails importés:', emails);
          // Ici vous pourriez ajouter la logique pour traiter les emails importés
          setShowCsvImport(false);
        }}
      />
    </div>
  );
}

