import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { 
  MessageSquare, 
  Plus, 
  Send, 
  HelpCircle,
  MessageCircle,
  User,
  Shield
} from "lucide-react";
import { toast } from "sonner";
import { formatDistanceToNow } from "date-fns";
import { fr } from "date-fns/locale";
import { api } from "@/lib/api";
import { useAuth } from "@/hooks/useAuth";

interface Conversation {
  id: string;
  subject: string;
  status: 'open' | 'closed' | 'pending';
  priority: 'low' | 'medium' | 'high' | 'urgent';
  lastMessageAt: string;
  createdAt: string;
}

interface Message {
  id: string;
  senderId: string;
  senderType: 'admin' | 'organization';
  senderName: string;
  senderEmail: string;
  message: string;
  messageType: 'text' | 'system' | 'attachment';
  read: boolean;
  createdAt: string;
}

export default function Support() {
  const { organization } = useAuth();
  const [conversations, setConversations] = useState<Conversation[]>([]);
  const [selectedConversation, setSelectedConversation] = useState<string | null>(null);
  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [showNewConversation, setShowNewConversation] = useState(false);
  const [newConversationData, setNewConversationData] = useState({
    subject: '',
    message: '',
    priority: 'medium' as 'low' | 'medium' | 'high' | 'urgent'
  });
  const [loading, setLoading] = useState(true);
  const [sending, setSending] = useState(false);

  useEffect(() => {
    fetchConversations();
  }, []);

  useEffect(() => {
    if (selectedConversation) {
      fetchMessages(selectedConversation);
    }
  }, [selectedConversation]);

  const fetchConversations = async () => {
    try {
      const response = await api.admin.getConversations();

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Erreur lors du chargement des conversations');
      }

      const data = await response.json();
      setConversations(data);
    } catch (error: any) {
      console.error('Error fetching conversations:', error);
      toast.error(error.message || 'Erreur lors du chargement des conversations');
    } finally {
      setLoading(false);
    }
  };

  const fetchMessages = async (conversationId: string) => {
    try {
      const response = await api.admin.getMessages(conversationId);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Erreur lors du chargement des messages');
      }

      const data = await response.json();
      setMessages(data);
    } catch (error: any) {
      console.error('Error fetching messages:', error);
      toast.error(error.message || 'Erreur lors du chargement des messages');
    }
  };

  const createNewConversation = async () => {
    if (!newConversationData.subject.trim() || !newConversationData.message.trim()) {
      toast.error('Veuillez remplir tous les champs obligatoires');
      return;
    }

    setSending(true);
    try {
      const response = await api.admin.createConversation(newConversationData);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Erreur lors de la création de la conversation');
      }

      const conversation = await response.json();
      setConversations(prev => [conversation, ...prev]);
      setSelectedConversation(conversation.id);
      setShowNewConversation(false);
      setNewConversationData({ subject: '', message: '', priority: 'medium' });
      toast.success('Message envoyé avec succès');
    } catch (error: any) {
      console.error('Error creating conversation:', error);
      toast.error(error.message || 'Erreur lors de l\'envoi du message');
    } finally {
      setSending(false);
    }
  };

  const sendMessage = async () => {
    if (!newMessage.trim() || !selectedConversation) return;

    setSending(true);
    try {
      const response = await api.admin.sendMessage(selectedConversation, newMessage);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Erreur lors de l\'envoi du message');
      }

      const message = await response.json();
      setMessages(prev => [...prev, message]);
      setNewMessage('');
      // Mettre à jour la conversation dans la liste
      setConversations(prev => 
        prev.map(conv => 
          conv.id === selectedConversation 
            ? { ...conv, lastMessageAt: new Date().toISOString() }
            : conv
        )
      );
    } catch (error: any) {
      console.error('Error sending message:', error);
      toast.error(error.message || 'Erreur lors de l\'envoi du message');
    } finally {
      setSending(false);
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'open':
        return <Badge variant="secondary" className="bg-green-100 text-green-800">Ouverte</Badge>;
      case 'pending':
        return <Badge variant="secondary" className="bg-yellow-100 text-yellow-800">En attente</Badge>;
      case 'closed':
        return <Badge variant="secondary" className="bg-gray-100 text-gray-800">Fermée</Badge>;
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  const getPriorityBadge = (priority: string) => {
    switch (priority) {
      case 'urgent':
        return <Badge variant="destructive">Urgent</Badge>;
      case 'high':
        return <Badge variant="secondary" className="bg-red-100 text-red-800">Haute</Badge>;
      case 'medium':
        return <Badge variant="secondary" className="bg-blue-100 text-blue-800">Moyenne</Badge>;
      case 'low':
        return <Badge variant="secondary" className="bg-gray-100 text-gray-800">Basse</Badge>;
      default:
        return <Badge variant="secondary">{priority}</Badge>;
    }
  };

  const selectedConv = conversations.find(c => c.id === selectedConversation);

  if (!organization) {
    return null;
  }

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 h-[600px]">
      {/* Conversations List */}
      <Card className="lg:col-span-1">
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <HelpCircle className="w-5 h-5" />
              Mes demandes
            </CardTitle>
            <Button
              size="sm"
              onClick={() => setShowNewConversation(true)}
            >
              <Plus className="w-4 h-4" />
            </Button>
          </div>
          <CardDescription>
            Contactez notre équipe de support pour toute question ou assistance
          </CardDescription>
        </CardHeader>
        <CardContent className="p-0">
          {loading ? (
            <div className="space-y-3 p-4">
              {Array.from({ length: 3 }).map((_, i) => (
                <div key={i} className="animate-pulse">
                  <div className="h-16 bg-gray-200 rounded"></div>
                </div>
              ))}
            </div>
          ) : (
            <div className="divide-y max-h-96 overflow-y-auto">
              {conversations.map((conversation) => (
                <div
                  key={conversation.id}
                  onClick={() => setSelectedConversation(conversation.id)}
                  className={`p-4 cursor-pointer hover:bg-gray-50 transition-colors ${
                    selectedConversation === conversation.id ? 'bg-blue-50 border-r-2 border-r-blue-500' : ''
                  }`}
                >
                  <div className="flex items-start justify-between">
                    <div className="flex-1 min-w-0">
                      <h4 className="text-sm font-medium truncate">
                        {conversation.subject}
                      </h4>
                      <div className="flex items-center gap-2 mt-1">
                        {getStatusBadge(conversation.status)}
                        {getPriorityBadge(conversation.priority)}
                      </div>
                      <p className="text-xs text-gray-500 mt-2">
                        {formatDistanceToNow(new Date(conversation.lastMessageAt), { 
                          addSuffix: true, 
                          locale: fr 
                        })}
                      </p>
                    </div>
                  </div>
                </div>
              ))}

              {conversations.length === 0 && (
                <div className="p-8 text-center text-gray-500">
                  <MessageCircle className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                  <p className="text-sm mb-2">Aucune conversation</p>
                  <p className="text-xs text-gray-400">Cliquez sur le bouton + pour contacter le support</p>
                </div>
              )}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Messages Area */}
      <Card className="lg:col-span-2">
        {showNewConversation ? (
          <div>
            <CardHeader>
              <CardTitle>Contacter le support</CardTitle>
              <CardDescription>
                Décrivez votre problème ou votre question. Notre équipe vous répondra dans les plus brefs délais.
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <Input
                placeholder="Sujet de votre demande"
                value={newConversationData.subject}
                onChange={(e) => setNewConversationData(prev => ({ ...prev, subject: e.target.value }))}
              />

              <div className="space-y-2">
                <label className="text-sm font-medium">Priorité</label>
                <select 
                  className="w-full p-2 border rounded-md"
                  value={newConversationData.priority}
                  onChange={(e) => setNewConversationData(prev => ({ 
                    ...prev, 
                    priority: e.target.value as 'low' | 'medium' | 'high' | 'urgent'
                  }))}
                >
                  <option value="low">Basse - Question générale</option>
                  <option value="medium">Moyenne - Problème technique mineur</option>
                  <option value="high">Haute - Problème technique important</option>
                  <option value="urgent">Urgente - Service indisponible</option>
                </select>
              </div>

              <Textarea
                placeholder="Décrivez votre problème en détail..."
                rows={6}
                value={newConversationData.message}
                onChange={(e) => setNewConversationData(prev => ({ ...prev, message: e.target.value }))}
              />

              <div className="flex gap-3">
                <Button 
                  onClick={createNewConversation}
                  disabled={sending || !newConversationData.subject.trim() || !newConversationData.message.trim()}
                  className="flex-1"
                >
                  <Send className="w-4 h-4 mr-2" />
                  {sending ? 'Envoi...' : 'Envoyer'}
                </Button>
                <Button 
                  variant="outline" 
                  onClick={() => setShowNewConversation(false)}
                  className="flex-1"
                >
                  Annuler
                </Button>
              </div>
            </CardContent>
          </div>
        ) : selectedConv ? (
          <div className="flex flex-col h-full">
            <CardHeader className="pb-3">
              <div className="flex items-center justify-between">
                <div>
                  <CardTitle className="text-lg">{selectedConv.subject}</CardTitle>
                  <div className="flex items-center gap-2 mt-1">
                    {getStatusBadge(selectedConv.status)}
                    {getPriorityBadge(selectedConv.priority)}
                  </div>
                </div>
              </div>
            </CardHeader>

            {/* Messages */}
            <CardContent className="flex-1 flex flex-col min-h-0">
              <div className="flex-1 overflow-y-auto space-y-4 mb-4">
                {messages.map((message) => (
                  <div
                    key={message.id}
                    className={`flex gap-3 ${
                      message.senderType === 'organization' ? 'justify-end' : 'justify-start'
                    }`}
                  >
                    <div
                      className={`max-w-[70%] rounded-lg p-3 ${
                        message.senderType === 'organization'
                          ? 'bg-blue-500 text-white'
                          : message.messageType === 'system'
                          ? 'bg-gray-100 text-gray-600 text-center italic'
                          : 'bg-gray-100 text-gray-900'
                      }`}
                    >
                      {message.messageType !== 'system' && (
                        <div className="flex items-center gap-2 mb-1">
                          {message.senderType === 'admin' ? (
                            <Shield className="w-4 h-4" />
                          ) : (
                            <User className="w-4 h-4" />
                          )}
                          <span className="text-xs font-medium">
                            {message.senderType === 'admin' ? 'Support TeamMove' : 'Vous'}
                          </span>
                        </div>
                      )}
                      <p className="text-sm whitespace-pre-wrap">{message.message}</p>
                      <p className={`text-xs mt-2 ${
                        message.senderType === 'organization' ? 'text-blue-100' : 'text-gray-500'
                      }`}>
                        {formatDistanceToNow(new Date(message.createdAt), { 
                          addSuffix: true, 
                          locale: fr 
                        })}
                      </p>
                    </div>
                  </div>
                ))}
              </div>

              {/* Message Input */}
              {selectedConv.status !== 'closed' && (
                <div className="flex gap-2">
                  <Textarea
                    placeholder="Tapez votre message..."
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    rows={2}
                    className="flex-1"
                    onKeyDown={(e) => {
                      if (e.key === 'Enter' && !e.shiftKey) {
                        e.preventDefault();
                        sendMessage();
                      }
                    }}
                  />
                  <Button
                    onClick={sendMessage}
                    disabled={!newMessage.trim() || sending}
                    className="self-end"
                  >
                    <Send className="w-4 h-4" />
                  </Button>
                </div>
              )}

              {selectedConv.status === 'closed' && (
                <div className="text-center p-4 bg-gray-50 rounded-lg">
                  <p className="text-sm text-gray-500">
                    Cette conversation a été fermée. Si vous avez d'autres questions, 
                    n'hésitez pas à créer une nouvelle demande.
                  </p>
                </div>
              )}
            </CardContent>
          </div>
        ) : (
          <CardContent className="flex items-center justify-center h-full text-gray-500">
            <div className="text-center">
              <MessageSquare className="w-12 h-12 mx-auto mb-4" />
              <h3 className="font-medium mb-2">Besoin d'aide ?</h3>
              <p className="text-sm mb-4">
                Notre équipe de support est là pour vous aider. 
                Sélectionnez une conversation existante ou créez-en une nouvelle.
              </p>
              <Button onClick={() => setShowNewConversation(true)}>
                <Plus className="w-4 h-4 mr-2" />
                Contacter le support
              </Button>
            </div>
          </CardContent>
        )}
      </Card>
    </div>
  );
}

client/src/components/debug/BackendTestP.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { api } from "@/lib/api";

interface TestResult {
  name: string;
  status: 'success' | 'error' | 'loading';
  data?: any;
  error?: string;
  timestamp?: string;
}

export default function BackendTestPanel() {
  const [tests, setTests] = useState<TestResult[]>([]);
  const { toast } = useToast();

  const runTest = async (testName: string, testFn: () => Promise<Response>) => {
    setTests(prev => [...prev.filter(t => t.name !== testName), {
      name: testName,
      status: 'loading'
    }]);

    try {
      const response = await testFn();
      const data = await response.json();

      setTests(prev => [...prev.filter(t => t.name !== testName), {
        name: testName,
        status: response.ok ? 'success' : 'error',
        data: response.ok ? data : undefined,
        error: !response.ok ? data.message || 'Erreur inconnue' : undefined,
        timestamp: new Date().toLocaleTimeString()
      }]);

      if (response.ok) {
        toast({
          title: `✅ Test ${testName} réussi`,
          description: "La communication avec le backend fonctionne"
        });
      } else {
        toast({
          title: `❌ Test ${testName} échoué`,
          description: data.message || 'Erreur de communication',
          variant: "destructive"
        });
      }
    } catch (error) {
      setTests(prev => [...prev.filter(t => t.name !== testName), {
        name: testName,
        status: 'error',
        error: error instanceof Error ? error.message : 'Erreur réseau',
        timestamp: new Date().toLocaleTimeString()
      }]);

      toast({
        title: `❌ Test ${testName} échoué`,
        description: "Erreur de connexion au backend",
        variant: "destructive"
      });
    }
  };

  const testEndpoints = [
    {
      name: 'Health Check',
      description: 'Vérifier l\'état du serveur',
      fn: () => api.health(),
      icon: '🏥'
    },
    {
      name: 'Backend Test',
      description: 'Test de communication backend',
      fn: () => api.test(),
      icon: '🧪'
    },
    {
      name: 'Liste des utilisateurs',
      description: 'Afficher les utilisateurs de la base',
      fn: () => api.debugUsers(),
      icon: '👥'
    }
  ];

  const getStatusBadge = (status: TestResult['status']) => {
    switch (status) {
      case 'success':
        return <Badge className="bg-green-500">✅ Succès</Badge>;
      case 'error':
        return <Badge variant="destructive">❌ Erreur</Badge>;
      case 'loading':
        return <Badge variant="secondary">⏳ En cours...</Badge>;
      default:
        return null;
    }
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <span>🔧</span>
            <span>Tests de Communication Backend</span>
          </CardTitle>
          <CardDescription>
            Testez la communication entre le frontend et le backend pour diagnostiquer les problèmes
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid gap-4">
            {testEndpoints.map((test) => (
              <div key={test.name} className="flex items-center justify-between p-4 border rounded-lg">
                <div className="flex items-center space-x-3">
                  <span className="text-2xl">{test.icon}</span>
                  <div>
                    <h4 className="font-medium">{test.name}</h4>
                    <p className="text-sm text-gray-500">{test.description}</p>
                  </div>
                </div>
                <Button
                  onClick={() => runTest(test.name, test.fn)}
                  variant="outline"
                  size="sm"
                  disabled={tests.find(t => t.name === test.name)?.status === 'loading'}
                >
                  Tester
                </Button>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {tests.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle>Résultats des Tests</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {tests.map((test) => (
                <div key={`${test.name}-${test.timestamp}`} className="border rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <span className="font-medium">{test.name}</span>
                    <div className="flex items-center space-x-2">
                      {getStatusBadge(test.status)}
                      {test.timestamp && (
                        <span className="text-xs text-gray-500">{test.timestamp}</span>
                      )}
                    </div>
                  </div>

                  {test.error && (
                    <div className="bg-red-50 border border-red-200 rounded p-3 mb-2">
                      <p className="text-red-700 text-sm font-medium">Erreur:</p>
                      <p className="text-red-600 text-sm">{test.error}</p>
                    </div>
                  )}

                  {test.data && (
                    <div className="bg-green-50 border border-green-200 rounded p-3">
                      <p className="text-green-700 text-sm font-medium mb-2">Réponse:</p>
                      <pre className="text-xs bg-white p-2 rounded border overflow-auto max-h-40">
                        {JSON.stringify(test.data, null, 2)}
                      </pre>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

