import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { api } from "@/lib/api";
import { CheckCircle } from "lucide-react";

interface ForgotPasswordModalProps {
  isOpen: boolean;
  onClose: () => void;
}

export default function ForgotPasswordModal({ isOpen, onClose }: ForgotPasswordModalProps) {
  const [email, setEmail] = useState("");
  const [isSuccess, setIsSuccess] = useState(false);
  const { toast } = useToast();

  const forgotPasswordMutation = useMutation({
    mutationFn: (email: string) => 
      fetch("/api/forgot-password", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email }),
      }).then(async res => {
        const data = await res.json();
        if (!res.ok) {
          throw new Error(data.message || "Une erreur s'est produite");
        }
        return data;
      }),
    onSuccess: () => {
      setIsSuccess(true);
      toast({
        title: "Email envoyé",
        description: "Si un compte existe avec cette adresse, un email de réinitialisation a été envoyé.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Erreur",
        description: error.message || "Une erreur s'est produite lors de l'envoi de l'email.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!email) {
      toast({
        title: "Erreur",
        description: "Veuillez saisir votre adresse email.",
        variant: "destructive",
      });
      return;
    }

    forgotPasswordMutation.mutate(email);
  };

  const handleClose = () => {
    setEmail("");
    setIsSuccess(false);
    onClose();
  };

  if (isSuccess) {
    return (
      <Dialog open={isOpen} onOpenChange={handleClose}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle className="text-2xl font-bold text-gray-900 flex items-center">
              <CheckCircle className="text-green-500 mr-2" size={24} />
              Email envoyé
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-4 text-center">
            <p className="text-gray-600">
              Si un compte existe avec l'adresse <strong>{email}</strong>, 
              vous recevrez sous peu un email contenant les instructions 
              pour réinitialiser votre mot de passe.
            </p>

            <p className="text-sm text-gray-500">
              Vérifiez également votre dossier de spams/courriers indésirables.
            </p>

            <Button onClick={handleClose} className="w-full">
              Fermer
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="text-2xl font-bold text-gray-900">
            Mot de passe oublié
          </DialogTitle>
        </DialogHeader>

        <div className="text-sm text-gray-600 mb-4">
          Saisissez votre adresse email et nous vous enverrons un lien 
          pour réinitialiser votre mot de passe.
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label htmlFor="reset-email">Adresse email</Label>
            <Input 
              id="reset-email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="votre@email.com"
              required
            />
          </div>

          <Button 
            type="submit" 
            className="w-full bg-primary hover:bg-blue-700"
            disabled={forgotPasswordMutation.isPending}
          >
            {forgotPasswordMutation.isPending ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                Envoi en cours...
              </>
            ) : (
              "Envoyer le lien de réinitialisation"
            )}
          </Button>

          <Button 
            type="button"
            variant="outline"
            onClick={handleClose}
            className="w-full"
          >
            Annuler
          </Button>
        </form>
      </DialogContent>
    </Dialog>
  );
}

