import { useState, useEffect } from "react";
import { useQueryClient } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import ForgotPasswordModal from "./ForgotPasswordModal";
import ModernCaptcha from "@/components/captcha/ModernCaptcha";

interface LoginModalProps {
  isOpen: boolean;
  onClose: () => void;
  onShowRegistration: () => void;
}

export default function LoginModal({ isOpen, onClose, onShowRegistration }: LoginModalProps) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isCaptchaVerified, setIsCaptchaVerified] = useState(false);
  const [showCaptcha, setShowCaptcha] = useState(false);
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const { login } = useAuth();

  // Get queryClient safely
  let queryClient;
  try {
    queryClient = useQueryClient();
  } catch (error) {
    console.warn("QueryClient not available in LoginModal:", error);
    queryClient = null;
  }

  // Load remembered email on component mount
  useEffect(() => {
    const rememberedEmail = localStorage.getItem('userEmail');
    const wasRemembered = localStorage.getItem('rememberMe') === 'true';

    if (rememberedEmail && wasRemembered) {
      setEmail(rememberedEmail);
      setRememberMe(true);
    }
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!email || !password) {
      toast({
        title: "Erreur",
        description: "Veuillez remplir tous les champs.",
        variant: "destructive",
      });
      return;
    }

    // Show captcha after first attempt or if there were previous failed attempts
    const failedAttempts = parseInt(localStorage.getItem('loginFailedAttempts') || '0');
    if (failedAttempts > 0 || showCaptcha) {
      if (!isCaptchaVerified) {
        setShowCaptcha(true);
        toast({
          title: "Vérification requise",
          description: "Veuillez compléter la vérification humaine.",
          variant: "destructive",
        });
        return;
      }
    }

    setIsLoading(true);

    try {
      const result = await login(email, password, rememberMe);

      if (result.success) {
        // Store remember me preference in localStorage
        if (rememberMe) {
          localStorage.setItem('rememberMe', 'true');
          localStorage.setItem('userEmail', email);
        } else {
          localStorage.removeItem('rememberMe');
          localStorage.removeItem('userEmail');
        }

        // Invalidate queries to refresh user data
        if (queryClient) {
          queryClient.invalidateQueries({ queryKey: ["/api/me"] });
        }

        // Clear failed attempts on successful login
        localStorage.removeItem('loginFailedAttempts');

        // Navigate to dashboard
        setLocation("/dashboard");
        onClose();

        toast({
          title: "Connexion réussie",
          description: rememberMe ? 
            "Vous resterez connecté pendant 7 jours." : 
            "Vous êtes maintenant connecté.",
        });
      } else {
        // Increment failed attempts
        const currentAttempts = parseInt(localStorage.getItem('loginFailedAttempts') || '0');
        localStorage.setItem('loginFailedAttempts', (currentAttempts + 1).toString());

        // Show captcha after 2 failed attempts
        if (currentAttempts >= 1) {
          setShowCaptcha(true);
          setIsCaptchaVerified(false);
        }

        toast({
          title: "Erreur de connexion",
          description: result.error || "Email ou mot de passe incorrect.",
          variant: "destructive",
        });
      }
    } catch (error: any) {
      console.error("Login error:", error);
      toast({
        title: "Erreur de connexion",
        description: error.message || "Une erreur inattendue s'est produite.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="text-2xl font-bold text-gray-900">Connexion</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label htmlFor="email">Email</Label>
            <Input 
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="votre@email.com"
              required
              disabled={isLoading}
            />
          </div>

          <div>
            <Label htmlFor="password">Mot de passe</Label>
            <Input 
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="••••••••"
              required
              disabled={isLoading}
            />
          </div>

          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Checkbox 
                id="remember"
                checked={rememberMe}
                onCheckedChange={(checked) => setRememberMe(checked === true)}
                disabled={isLoading}
              />
              <Label htmlFor="remember" className="text-sm text-gray-600">
                Se souvenir de moi
              </Label>
            </div>
            <Button 
              variant="link" 
              className="text-sm text-primary hover:text-blue-700 p-0"
              onClick={() => setShowForgotPassword(true)}
              type="button"
              disabled={isLoading}
            >
              Mot de passe oublié ?
            </Button>
          </div>

          {showCaptcha && (
            <ModernCaptcha 
              onVerify={setIsCaptchaVerified}
              className="my-4"
            />
          )}

          <Button 
            type="submit" 
            className="w-full bg-primary hover:bg-blue-700"
            disabled={isLoading || (showCaptcha && !isCaptchaVerified)}
          >
            {isLoading ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                Connexion...
              </>
            ) : (
              "Se connecter"
            )}
          </Button>
        </form>

        <div className="text-center">
          <p className="text-gray-600">
            Pas encore de compte ?{" "}
            <Button 
              variant="link" 
              onClick={onShowRegistration} 
              className="text-primary hover:text-blue-700 p-0"
              disabled={isLoading}
            >
              S'inscrire
            </Button>
          </p>
        </div>
      </DialogContent>

      <ForgotPasswordModal
        isOpen={showForgotPassword}
        onClose={() => setShowForgotPassword(false)}
      />
    </Dialog>
  );
}

