import { useState, useEffect } from "react";
import { loadStripe } from "@stripe/stripe-js";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";

// Initialiser Stripe avec votre clé publique
const stripePromise = loadStripe(import.meta.env.VITE_STRIPE_PUBLIC_KEY || "pk_test_51S3zPW8XM0zNL8ZMhdeyjgfrp4eKmTmzKRMqRENFwKoUjfSByTeGfWXfcf6Vr6FF9FJSBauMvjTp6cnDFDJwfPxN00VrImPBXj");

interface PaymentSetupProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess?: () => void;
  targetPlan?: string; // Plan cible depuis l'inscription
}

interface SubscriptionPlan {
  id: string;
  name: string;
  price: number;
  type: 'payment' | 'subscription';
  interval?: 'monthly' | 'annual' | 'pack_single' | 'pack_10';
  features: string[];
  description: string;
  badge?: string;
  badgeColor?: string;
}

const subscriptionPlans: SubscriptionPlan[] = [
  // Offres événementielles (paiements uniques)
  {
    id: "evenementielle-single",
    name: "Pack Événement",
    price: 1500, // 15€
    type: "payment",
    interval: "pack_single", 
    description: "Idéal pour organiser un événement ponctuel",
    badge: "POPULAIRE",
    badgeColor: "bg-orange-100 text-orange-800",
    features: [
      "1 événement complet",
      "Profil de personnalisation",
      "Gestion conducteurs/passagers", 
      "Messagerie intégrée",
      "Suivi en temps réel",
      "Support prioritaire"
    ]
  },
  {
    id: "evenementielle-pack10",
    name: "Pack 10 Événements",
    price: 15000, // 150€
    type: "payment",
    interval: "pack_10",
    description: "Parfait pour les organisateurs réguliers",
    badge: "ÉCONOMIQUE",
    badgeColor: "bg-green-100 text-green-800",
    features: [
      "10 événements complets",
      "Profil de personnalisation",
      "Gestion conducteurs/passagers",
      "Messagerie intégrée", 
      "Suivi en temps réel",
      "Support prioritaire",
      "Valable 12 mois"
    ]
  },
  // Formules Pro (abonnements mensuels)
  {
    id: "pro-club",
    name: "Clubs & Associations", 
    price: 1999, // 19,99€
    type: "subscription",
    interval: "monthly",
    description: "Conçu pour les clubs sportifs et associations",
    badge: "PRO",
    badgeColor: "bg-blue-100 text-blue-800",
    features: [
      "Événements illimités",
      "Invitations illimitées",
      "Profil de personnalisation avancé",
      "Gestion multi-conducteurs",
      "Messagerie avancée",
      "Suivi en temps réel",
      "Statistiques détaillées",
      "Support prioritaire",
      "API d'intégration",
      "Branding personnalisé"
    ]
  },
  {
    id: "pro-pme",
    name: "PME",
    price: 4900, // 49€
    type: "subscription", 
    interval: "monthly",
    description: "Idéal pour les petites et moyennes entreprises",
    badge: "BUSINESS",
    badgeColor: "bg-purple-100 text-purple-800",
    features: [
      "Tout de Clubs & Associations",
      "Multi-utilisateurs (5 admins)",
      "Gestion des équipes", 
      "Reporting avancé",
      "Intégrations tierces",
      "Support téléphonique",
      "Formation personnalisée",
      "SLA garanti"
    ]
  },
  {
    id: "pro-entreprise",
    name: "Grandes Entreprises",
    price: 9900, // 99€
    type: "subscription",
    interval: "monthly", 
    description: "Solution entreprise complète et sur-mesure",
    badge: "ENTERPRISE", 
    badgeColor: "bg-yellow-100 text-yellow-800",
    features: [
      "Tout de PME",
      "Multi-utilisateurs illimités",
      "Gestion multi-sites",
      "API complète",
      "SSO/SAML",
      "Hébergement dédié (option)",
      "Support 24/7",
      "Account Manager dédié",
      "Personnalisation complète",
      "Conformité RGPD avancée"
    ]
  }
];

export default function PaymentSetup({ isOpen, onClose, onSuccess, targetPlan }: PaymentSetupProps) {
  const [selectedPlan, setSelectedPlan] = useState<string>(targetPlan || "evenementielle-single");
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  // Mettre à jour le plan sélectionné quand targetPlan change
  useEffect(() => {
    if (targetPlan) {
      setSelectedPlan(targetPlan);
    }
  }, [targetPlan]);

  const handleSubscribe = async () => {
    setIsLoading(true);

    try {
      console.log('🎯 Début du processus de paiement pour le plan:', selectedPlan);

      const stripe = await stripePromise;
      if (!stripe) {
        throw new Error("Stripe n'a pas pu être initialisé. Vérifiez votre configuration Stripe.");
      }

      const plan = subscriptionPlans.find(p => p.id === selectedPlan);
      if (!plan) {
        throw new Error(`Plan non trouvé: ${selectedPlan}`);
      }

      console.log('📦 Plan sélectionné:', plan);

      // Créer la session de checkout
      console.log('📡 Appel API /api/subscriptions/create...');
      const response = await fetch('/api/subscriptions/create', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          planId: plan.id,
          successUrl: `${window.location.origin}/dashboard?payment_success=true`,
          cancelUrl: `${window.location.origin}/dashboard?payment_cancelled=true`,
        }),
      });

      console.log('📡 Réponse API status:', response.status);

      if (!response.ok) {
        const errorText = await response.text();
        console.error('❌ Erreur HTTP:', response.status, errorText);
        throw new Error(`Erreur serveur (${response.status}): ${errorText}`);
      }

      const data = await response.json();
      console.log('📡 Données reçues:', data);

      if (data.error || data.message) {
        throw new Error(data.error || data.message);
      }

      // Vérifier la présence des données nécessaires
      if (!data.url && !data.sessionId) {
        console.error('❌ Données manquantes dans la réponse:', data);
        throw new Error("Réponse serveur invalide: aucune URL de redirection fournie");
      }

      // Rediriger vers l'URL de checkout Stripe moderne
      if (data.url) {
        console.log('🚀 Redirection vers Stripe Checkout:', data.url);
        window.location.href = data.url;
      } else if (data.sessionId) {
        console.log('🚀 Redirection Stripe avec sessionId:', data.sessionId);
        // Fallback: utiliser l'ancienne méthode si l'URL n'est pas disponible
        const { error: stripeError } = await stripe.redirectToCheckout({
          sessionId: data.sessionId,
        });
        if (stripeError) {
          console.error('❌ Erreur Stripe redirectToCheckout:', stripeError);
          throw new Error(stripeError.message);
        }
      }
    } catch (error: any) {
      console.error('❌ Erreur complète de paiement:', error);

      let errorMessage = "Une erreur est survenue lors de la configuration du paiement.";

      if (error.message) {
        errorMessage = error.message;
      }

      // Messages d'erreur plus spécifiques
      if (error.message?.includes('fetch')) {
        errorMessage = "Impossible de contacter le serveur. Vérifiez votre connexion internet.";
      } else if (error.message?.includes('Stripe')) {
        errorMessage = `Erreur Stripe: ${error.message}`;
      }

      toast({
        title: "Erreur de paiement",
        description: errorMessage,
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const formatPrice = (priceInCents: number, interval?: string) => {
    const price = (priceInCents / 100).toFixed(2);

    if (interval === 'monthly') return `${price}€/mois`;
    if (interval === 'pack_single') return `${price}€`;
    if (interval === 'pack_10') return `${price}€`;

    return `${price}€`;
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-2xl font-bold text-center">
            🎉 Activez votre abonnement
          </DialogTitle>
          <p className="text-center text-muted-foreground">
            Choisissez votre offre et commencez à profiter de toutes les fonctionnalités
          </p>
        </DialogHeader>

        <div className="space-y-6">
          <RadioGroup
            value={selectedPlan}
            onValueChange={setSelectedPlan}
            className="grid lg:grid-cols-3 md:grid-cols-2 gap-6"
          >
            {subscriptionPlans.map((plan) => (
              <Label
                key={plan.id}
                htmlFor={plan.id}
                className={`relative cursor-pointer block transition-all duration-200 ${
                  selectedPlan === plan.id
                    ? 'ring-2 ring-primary ring-offset-2'
                    : 'hover:shadow-lg'
                }`}
              >
                <RadioGroupItem
                  value={plan.id}
                  id={plan.id}
                  className="sr-only"
                />
                <Card className={`h-full border-2 transition-all duration-200 ${
                  selectedPlan === plan.id
                    ? 'border-primary shadow-lg bg-primary/5'
                    : 'border-border'
                }`}>
                  <CardHeader>
                    <div className="flex items-center justify-between">
                      <CardTitle className="text-lg">{plan.name}</CardTitle>
                      {plan.badge && (
                        <Badge className={plan.badgeColor}>
                          {plan.badge}
                        </Badge>
                      )}
                    </div>
                    <div className="space-y-2">
                      <div className="text-2xl font-bold">
                        {formatPrice(plan.price, plan.interval)}
                      </div>
                      {plan.interval === 'pack_10' && (
                        <div className="text-sm text-muted-foreground">
                          Soit 15€ par événement
                        </div>
                      )}
                    </div>
                    <CardDescription className="text-sm">
                      {plan.description}
                    </CardDescription>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-2">
                      {plan.features.slice(0, 5).map((feature, index) => (
                        <div key={index} className="flex items-center text-sm">
                          <i className="fas fa-check text-green-500 mr-2 w-3"></i>
                          <span>{feature}</span>
                        </div>
                      ))}
                      {plan.features.length > 5 && (
                        <div className="text-xs text-muted-foreground mt-2">
                          + {plan.features.length - 5} autres fonctionnalités
                        </div>
                      )}
                    </div>
                  </CardContent>
                </Card>
              </Label>
            ))}
          </RadioGroup>

          <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 dark:bg-blue-950 dark:border-blue-800">
            <div className="flex items-start space-x-3">
              <i className="fas fa-shield-alt text-blue-500 mt-1"></i>
              <div className="text-sm text-blue-700 dark:text-blue-300">
                <p className="font-medium mb-1">Paiement 100% sécurisé</p>
                <ul className="space-y-1 text-xs">
                  <li>• Chiffrement SSL et protocoles de sécurité Stripe</li>
                  <li>• Aucune donnée de carte bancaire stockée sur nos serveurs</li>
                  <li>• Résiliation possible à tout moment depuis votre dashboard</li>
                  <li>• Remboursement sous 14 jours si vous n'êtes pas satisfait</li>
                </ul>
              </div>
            </div>
          </div>

          <div className="flex space-x-4">
            <Button
              variant="outline"
              onClick={onClose}
              className="flex-1"
              disabled={isLoading}
            >
              Annuler
            </Button>
            <Button
              onClick={handleSubscribe}
              className="flex-1 bg-primary hover:bg-primary/90"
              disabled={isLoading}
            >
              {isLoading ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Configuration...
                </>
              ) : (
                <>
                  <i className="fas fa-credit-card mr-2"></i>
                  Procéder au paiement
                </>
              )}
            </Button>
          </div>

          <div className="text-xs text-center text-muted-foreground">
            En continuant, vous acceptez nos conditions de service et notre politique de remboursement.
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/subscription/Strip.content:
import React, { useState, useEffect } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import {
  Elements,
  CardElement,
  useStripe,
  useElements,
  PaymentElement,
  AddressElement,
  LinkAuthenticationElement,
} from '@stripe/react-stripe-js';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/card';
import { Button } from '../ui/button';
import { Badge } from '../ui/badge';
import { Input } from '../ui/input';
import { Label } from '../ui/label';
import { Separator } from '../ui/separator';
import { 
  CreditCard, 
  Lock, 
  Shield, 
  CheckCircle, 
  AlertCircle,
  User,
  Mail,
  MapPin,
  Calendar
} from 'lucide-react';
import { toast } from 'sonner';
import { cn } from '../../lib/utils';

// Configuration Stripe
const stripePromise = loadStripe(
  import.meta.env.VITE_STRIPE_PUBLIC_KEY || 
  'pk_test_51S3zPW8XM0zNL8ZMhdeyjgfrp4eKmTmzKRMqRENFwKoUjfSByTeGfWXfcf6Vr6FF9FJSBauMvjTp6cnDFDJwfPxN00VrImPBXj'
);

// Types
interface PaymentFormProps {
  planId: string;
  planName: string;
  planPrice: number;
  currency: string;
  billingInterval: string;
  onSuccess: () => void;
  onCancel: () => void;
}

interface PaymentFormData {
  email?: string;
  name?: string;
  address?: {
    line1: string;
    line2?: string;
    city: string;
    state: string;
    postal_code: string;
    country: string;
  };
}

// Styles personnalisés pour Stripe Elements
const stripeElementsOptions = {
  appearance: {
    theme: 'stripe' as const,
    variables: {
      colorPrimary: '#3b82f6',
      colorBackground: '#ffffff',
      colorText: '#1f2937',
      colorDanger: '#ef4444',
      fontFamily: 'system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
      spacingUnit: '4px',
      borderRadius: '6px',
    },
    rules: {
      '.Input': {
        border: '1px solid #d1d5db',
        boxShadow: '0 1px 2px 0 rgba(0, 0, 0, 0.05)',
      },
      '.Input:focus': {
        border: '1px solid #3b82f6',
        boxShadow: '0 0 0 3px rgba(59, 130, 246, 0.1)',
      },
      '.Label': {
        fontWeight: '500',
        fontSize: '14px',
        color: '#374151',
      },
    },
  },
};

// Composant principal du formulaire de paiement
function PaymentForm({ planId, planName, planPrice, currency, billingInterval, onSuccess, onCancel }: PaymentFormProps) {
  const stripe = useStripe();
  const elements = useElements();

  const [isProcessing, setIsProcessing] = useState(false);
  const [paymentError, setPaymentError] = useState<string | null>(null);
  const [formData, setFormData] = useState<PaymentFormData>({});
  const [clientSecret, setClientSecret] = useState<string>('');
  const [paymentElementReady, setPaymentElementReady] = useState(false);

  // Initialiser l'intention de paiement
  useEffect(() => {
    const initializePayment = async () => {
      try {
        const response = await fetch('/api/stripe/create-payment-intent', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include',
          body: JSON.stringify({
            planId,
            amount: planPrice,
            currency: currency.toLowerCase(),
            automatic_payment_methods: { enabled: true },
          }),
        });

        if (!response.ok) {
          throw new Error('Erreur lors de la création de l\'intention de paiement');
        }

        const { client_secret } = await response.json();
        setClientSecret(client_secret);
      } catch (error: any) {
        console.error('Erreur initialisation paiement:', error);
        setPaymentError(error.message);
        toast.error(error.message);
      }
    };

    initializePayment();
  }, [planId, planPrice, currency]);

  // Formatage du prix
  const formatPrice = (price: number, curr: string = 'EUR') => {
    return new Intl.NumberFormat('fr-FR', {
      style: 'currency',
      currency: curr,
    }).format(price / 100);
  };

  // Gestion de la soumission du formulaire
  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();

    if (!stripe || !elements) {
      toast.error('Stripe n\'est pas encore chargé. Veuillez patienter.');
      return;
    }

    if (!paymentElementReady) {
      toast.error('Le formulaire de paiement n\'est pas prêt. Veuillez patienter.');
      return;
    }

    setIsProcessing(true);
    setPaymentError(null);

    try {
      // Confirmer le paiement avec Stripe
      const { error, paymentIntent } = await stripe.confirmPayment({
        elements,
        confirmParams: {
          return_url: `${window.location.origin}/dashboard?payment=success`,
          payment_method_data: {
            billing_details: {
              name: formData.name,
              email: formData.email,
              address: formData.address,
            },
          },
        },
        redirect: 'if_required',
      });

      if (error) {
        console.error('Erreur paiement Stripe:', error);
        setPaymentError(error.message || 'Erreur lors du paiement');
        toast.error(error.message || 'Erreur lors du paiement');
      } else if (paymentIntent && paymentIntent.status === 'succeeded') {
        // Paiement réussi
        toast.success('Paiement réussi ! Activation de votre abonnement...');

        // Notifier le succès au parent
        setTimeout(() => {
          onSuccess();
        }, 1500);
      } else {
        // Statut inattendu
        console.warn('Statut de paiement inattendu:', paymentIntent?.status);
        setPaymentError('Statut de paiement inattendu');
      }
    } catch (error: any) {
      console.error('Erreur lors de la confirmation du paiement:', error);
      setPaymentError('Erreur technique lors du paiement');
      toast.error('Erreur technique lors du paiement');
    } finally {
      setIsProcessing(false);
    }
  };

  // Rendu du composant
  return (
    <div className="max-w-2xl mx-auto space-y-8">
      {/* Résumé de la commande */}
      <Card className="border-2 border-blue-100 bg-gradient-to-br from-blue-50 to-indigo-50">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Shield className="w-5 h-5 text-blue-500" />
            Résumé de votre commande
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="font-semibold text-lg">{planName}</h3>
              <p className="text-sm text-gray-600">
                {billingInterval === 'monthly' ? 'Abonnement mensuel' : 
                 billingInterval === 'annual' ? 'Abonnement annuel' :
                 billingInterval === 'pack_single' ? 'Pack événement unique' :
                 billingInterval === 'pack_10' ? 'Pack 10 événements' : 
                 'Paiement unique'}
              </p>
            </div>
            <div className="text-right">
              <div className="text-2xl font-bold text-blue-600">
                {formatPrice(planPrice, currency)}
              </div>
              <div className="text-sm text-gray-500">
                {billingInterval === 'monthly' && '/mois'}
                {billingInterval === 'annual' && '/an'}
              </div>
            </div>
          </div>

          <Separator />

          <div className="flex items-center gap-2 text-sm text-green-600">
            <CheckCircle className="w-4 h-4" />
            <span>Activation immédiate après paiement</span>
          </div>

          <div className="flex items-center gap-2 text-sm text-green-600">
            <CheckCircle className="w-4 h-4" />
            <span>Résiliation possible à tout moment</span>
          </div>
        </CardContent>
      </Card>

      {/* Formulaire de paiement */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <CreditCard className="w-5 h-5 text-gray-700" />
            Informations de paiement
          </CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Authentification par lien (email) */}
            <div className="space-y-2">
              <Label htmlFor="link-auth" className="flex items-center gap-2">
                <Mail className="w-4 h-4" />
                Adresse email
              </Label>
              <LinkAuthenticationElement
                id="link-auth"
                options={{
                  defaultValues: {
                    email: formData.email || '',
                  },
                }}
                onChange={(event) => {
                  if (event.value.email) {
                    setFormData(prev => ({
                      ...prev,
                      email: event.value.email,
                    }));
                  }
                }}
              />
            </div>

            <Separator />

            {/* Élément de paiement principal */}
            <div className="space-y-4">
              <Label className="flex items-center gap-2">
                <CreditCard className="w-4 h-4" />
                Moyens de paiement
              </Label>

              {clientSecret && (
                <div className="p-4 border border-gray-200 rounded-lg bg-gray-50">
                  <PaymentElement
                    id="payment-element"
                    options={{
                      layout: 'accordion',
                      defaultValues: {
                        billingDetails: {
                          name: formData.name,
                          email: formData.email,
                          address: formData.address,
                        },
                      },
                    }}
                    onReady={() => {
                      setPaymentElementReady(true);
                    }}
                    onChange={(event) => {
                      if (event.error) {
                        setPaymentError(event.error.message);
                      } else {
                        setPaymentError(null);
                      }
                    }}
                  />
                </div>
              )}
            </div>

            <Separator />

            {/* Adresse de facturation */}
            <div className="space-y-2">
              <Label className="flex items-center gap-2">
                <MapPin className="w-4 h-4" />
                Adresse de facturation
              </Label>
              <div className="p-4 border border-gray-200 rounded-lg bg-gray-50">
                <AddressElement
                  options={{
                    mode: 'billing',
                    allowedCountries: ['FR', 'BE', 'CH', 'CA', 'US'],
                    defaultValues: {
                      name: formData.name || '',
                      address: formData.address || {
                        line1: '',
                        city: '',
                        state: '',
                        postal_code: '',
                        country: 'FR',
                      },
                    },
                  }}
                  onChange={(event) => {
                    if (event.complete) {
                      setFormData(prev => ({
                        ...prev,
                        name: event.value.name,
                        address: event.value.address,
                      }));
                    }
                  }}
                />
              </div>
            </div>

            {/* Message d'erreur */}
            {paymentError && (
              <div className="flex items-center gap-2 p-4 bg-red-50 border border-red-200 rounded-lg text-red-700">
                <AlertCircle className="w-5 h-5" />
                <span>{paymentError}</span>
              </div>
            )}

            {/* Boutons d'action */}
            <div className="flex flex-col sm:flex-row gap-4 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={onCancel}
                disabled={isProcessing}
                className="flex-1"
              >
                Annuler
              </Button>

              <Button
                type="submit"
                disabled={!stripe || !paymentElementReady || isProcessing}
                className="flex-1 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700"
              >
                {isProcessing ? (
                  <>
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2" />
                    Traitement en cours...
                  </>
                ) : (
                  <>
                    <Lock className="w-4 h-4 mr-2" />
                    Payer {formatPrice(planPrice, currency)}
                  </>
                )}
              </Button>
            </div>

            {/* Informations de sécurité */}
            <div className="bg-gray-50 p-4 rounded-lg border">
              <div className="flex items-start gap-3">
                <Shield className="w-5 h-5 text-green-500 mt-0.5" />
                <div className="space-y-1">
                  <h4 className="font-medium text-sm">Paiement sécurisé</h4>
                  <p className="text-xs text-gray-600">
                    Vos informations de paiement sont cryptées et traitées de manière 
                    sécurisée par Stripe. Nous ne stockons jamais vos données de carte bancaire.
                  </p>
                  <div className="flex items-center gap-4 text-xs text-gray-500 mt-2">
                    <span className="flex items-center gap-1">
                      <CheckCircle className="w-3 h-3" />
                      Cryptage SSL
                    </span>
                    <span className="flex items-center gap-1">
                      <CheckCircle className="w-3 h-3" />
                      PCI DSS
                    </span>
                    <span className="flex items-center gap-1">
                      <CheckCircle className="w-3 h-3" />
                      Conformité RGPD
                    </span>
                  </div>
                </div>
              </div>
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

// Wrapper avec Elements Provider
export function StripePaymentForm(props: PaymentFormProps) {
  return (
    <Elements
      stripe={stripePromise}
      options={{
        ...stripeElementsOptions,
        mode: 'payment',
        currency: props.currency.toLowerCase(),
        amount: props.planPrice,
      }}
    >
      <PaymentForm {...props} />
    </Elements>
  );
}

export default StripePaymentForm;

client/src/components/subscription/Subsc.content:
import React, { useState, useEffect } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import {
  Elements,
  CardElement,
  useStripe,
  useElements,
  PaymentElement,
  EmbeddedCheckout,
  EmbeddedCheckoutProvider
} from '@stripe/react-stripe-js';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '../ui/card';
import { Button } from '../ui/button';
import { Badge } from '../ui/badge';
import { Separator } from '../ui/separator';
import { CheckCircle, CreditCard, Lock, Shield, Crown, Star, Sparkles } from 'lucide-react';
import { toast } from 'sonner';
import { cn } from '../../lib/utils';
import { api } from '../../lib/api';
import { useAuth } from '../../hooks/useAuth';

// Types pour les plans d'abonnement
interface SubscriptionPlan {
  id: string;
  name: string;
  type: 'decouverte' | 'evenementielle' | 'pro_club' | 'pro_pme' | 'pro_entreprise';
  price: number;
  currency: string;
  billingInterval: 'monthly' | 'annual' | 'pack_single' | 'pack_10';
  maxEvents: number | null;
  maxInvitations: number | null;
  validityMonths: number | null;
  features: string[];
  description: string;
  stripePriceId?: string;
  popular?: boolean;
  recommended?: boolean;
}

interface StripeCheckoutProps {
  plans: SubscriptionPlan[];
  currentPlan?: string;
  onPlanSelect: (planId: string) => void;
  loading?: boolean;
}

// Clé publique Stripe (à configurer dans les variables d'environnement)
const stripePromise = loadStripe(
  import.meta.env.VITE_STRIPE_PUBLIC_KEY || 
  'pk_test_51S3zPW8XM0zNL8ZMhdeyjgfrp4eKmTmzKRMqRENFwKoUjfSByTeGfWXfcf6Vr6FF9FJSBauMvjTp6cnDFDJwfPxN00VrImPBXj'
);

// Composant principal de checkout Stripe
export function SubscriptionCheckout({ plans, currentPlan, onPlanSelect, loading }: StripeCheckoutProps) {
  const [selectedPlan, setSelectedPlan] = useState<string | null>(null);
  const [checkoutMode, setCheckoutMode] = useState<'selection' | 'payment'>('selection');
  const [clientSecret, setClientSecret] = useState<string>('');
  const [isProcessing, setIsProcessing] = useState(false);

  // Fonction pour formater le prix
  const formatPrice = (price: number, currency: string = 'EUR') => {
    return new Intl.NumberFormat('fr-FR', {
      style: 'currency',
      currency: currency,
    }).format(price / 100);
  };

  // Fonction pour obtenir l'icône du plan
  const getPlanIcon = (type: string) => {
    switch (type) {
      case 'decouverte':
        return <Star className="w-6 h-6 text-blue-500" />;
      case 'evenementielle':
        return <Sparkles className="w-6 h-6 text-orange-500" />;
      case 'pro_club':
      case 'pro_pme':
      case 'pro_entreprise':
        return <Crown className="w-6 h-6 text-purple-500" />;
      default:
        return <Star className="w-6 h-6 text-gray-500" />;
    }
  };

  // Fonction pour obtenir l'intervalle de facturation formaté
  const getBillingInterval = (interval: string) => {
    switch (interval) {
      case 'monthly':
        return '/mois';
      case 'annual':
        return '/an';
      case 'pack_single':
        return ' (pack unique)';
      case 'pack_10':
        return ' (pack 10)';
      default:
        return '';
    }
  };

  // Gestion de la sélection d'un plan
  const handlePlanSelection = async (planId: string) => {
    if (loading || isProcessing) return;

    setSelectedPlan(planId);
    const plan = plans.find(p => p.id === planId);

    if (!plan || plan.type === 'decouverte') {
      // Plan gratuit - traitement immédiat
      onPlanSelect(planId);
      return;
    }

    setIsProcessing(true);

    try {
      // Utiliser l'API client avec authentification
      const successUrl = `${window.location.origin}/dashboard?payment=success&session_id={CHECKOUT_SESSION_ID}`;
      const cancelUrl = `${window.location.origin}/dashboard/subscription?payment=cancelled`;

      const response = await api.subscription.createPayment(planId, successUrl, cancelUrl);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Erreur lors de la création de la session de paiement');
      }

      const data = await response.json();

      if (data.url) {
        // Rediriger vers l'URL de checkout Stripe moderne
        window.location.href = data.url;
      } else if (data.sessionId) {
        // Utiliser le checkout intégré avec le sessionId
        setClientSecret(data.sessionId);
        setCheckoutMode('payment');
      } else {
        throw new Error("Aucune URL de redirection ou session ID fournie");
      }

    } catch (error: any) {
      console.error('Erreur lors de la création de la session:', error);
      toast.error(error.message || 'Erreur lors de la création de la session de paiement');
      setSelectedPlan(null);
    } finally {
      setIsProcessing(false);
    }
  };

  // Rendu du composant de sélection des plans
  const renderPlanSelection = () => (
    <div className="space-y-8">
      {/* En-tête */}
      <div className="text-center space-y-4">
        <h2 className="text-3xl font-bold text-gray-900">
          Choisissez votre plan
        </h2>
        <p className="text-lg text-gray-600 max-w-2xl mx-auto">
          Sélectionnez l'offre qui correspond le mieux à vos besoins. 
          Paiement sécurisé par Stripe.
        </p>
        <div className="flex items-center justify-center gap-2 text-sm text-gray-500">
          <Shield className="w-4 h-4" />
          <span>Paiement 100% sécurisé</span>
          <Lock className="w-4 h-4" />
          <span>Cryptage SSL</span>
        </div>
      </div>

      {/* Grille des plans */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 max-w-7xl mx-auto">
        {plans.map((plan) => (
          <Card
            key={plan.id}
            className={cn(
              "relative transition-all duration-300 cursor-pointer",
              "hover:shadow-xl hover:scale-105",
              plan.popular && "ring-2 ring-blue-500 shadow-lg",
              plan.recommended && "ring-2 ring-green-500 shadow-lg",
              selectedPlan === plan.id && "ring-2 ring-purple-500 shadow-lg",
              currentPlan === plan.id && "bg-gradient-to-br from-blue-50 to-indigo-50"
            )}
            onClick={() => handlePlanSelection(plan.id)}
          >
            {/* Badge pour les plans populaires/recommandés */}
            {plan.popular && (
              <Badge className="absolute -top-3 left-1/2 transform -translate-x-1/2 bg-blue-500 text-white">
                Populaire
              </Badge>
            )}
            {plan.recommended && (
              <Badge className="absolute -top-3 left-1/2 transform -translate-x-1/2 bg-green-500 text-white">
                Recommandé
              </Badge>
            )}

            <CardHeader className="text-center space-y-4">
              {/* Icône du plan */}
              <div className="flex justify-center">
                {getPlanIcon(plan.type)}
              </div>

              {/* Nom et description */}
              <div>
                <CardTitle className="text-xl font-bold">{plan.name}</CardTitle>
                <CardDescription className="text-sm">{plan.description}</CardDescription>
              </div>

              {/* Prix */}
              <div className="space-y-2">
                <div className="text-3xl font-bold text-gray-900">
                  {plan.price === 0 ? (
                    'Gratuit'
                  ) : (
                    <>
                      {formatPrice(plan.price)}
                      <span className="text-lg text-gray-500">
                        {getBillingInterval(plan.billingInterval)}
                      </span>
                    </>
                  )}
                </div>
                {plan.validityMonths && (
                  <div className="text-sm text-gray-500">
                    Valable {plan.validityMonths} mois
                  </div>
                )}
              </div>
            </CardHeader>

            <CardContent className="space-y-6">
              {/* Limites d'usage */}
              <div className="space-y-3">
                <div className="flex items-center justify-between text-sm">
                  <span>Événements</span>
                  <span className="font-semibold">
                    {plan.maxEvents === null ? 'Illimités' : plan.maxEvents}
                  </span>
                </div>
                <div className="flex items-center justify-between text-sm">
                  <span>Invitations</span>
                  <span className="font-semibold">
                    {plan.maxInvitations === null ? 'Illimitées' : plan.maxInvitations}
                  </span>
                </div>
              </div>

              <Separator />

              {/* Liste des fonctionnalités */}
              <div className="space-y-3">
                <h4 className="font-medium text-sm">Fonctionnalités incluses :</h4>
                <ul className="space-y-2">
                  {plan.features.map((feature, index) => (
                    <li key={index} className="flex items-start gap-2 text-sm">
                      <CheckCircle className="w-4 h-4 text-green-500 mt-0.5 flex-shrink-0" />
                      <span className="text-gray-700">{feature}</span>
                    </li>
                  ))}
                </ul>
              </div>

              {/* Bouton de sélection */}
              <Button
                className={cn(
                  "w-full mt-6",
                  plan.type === 'decouverte' && "bg-gray-600 hover:bg-gray-700",
                  plan.type === 'evenementielle' && "bg-orange-600 hover:bg-orange-700",
                  ['pro_club', 'pro_pme', 'pro_entreprise'].includes(plan.type) && 
                    "bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700"
                )}
                disabled={loading || isProcessing || currentPlan === plan.id}
              >
                {isProcessing && selectedPlan === plan.id ? (
                  <>
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2" />
                    Traitement...
                  </>
                ) : currentPlan === plan.id ? (
                  'Plan actuel'
                ) : plan.type === 'decouverte' ? (
                  'Choisir ce plan'
                ) : (
                  <>
                    <CreditCard className="w-4 h-4 mr-2" />
                    Souscrire maintenant
                  </>
                )}
              </Button>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Informations de sécurité */}
      <div className="text-center space-y-4 pt-8">
